        setShowEmojiPickerGeneric(false);
        setShowGenericTags(false);
        setShowKeyBoardGeneric(!showKeyBoardGeneric);

        //Specific
        setShowSpecificTags(false);
        setShowEmojiPicker(false);
        setShowKeyBoard(false);
