import React, { useEffect, useState } from 'react'

import { connect } from 'react-redux';
import { addProject, nextWizardStep,nextSpecificWizardStep, resetWizardStep, setWizardSelectedPage, } from '../../../redux/actions/wizardActions';
import { getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import WizardRadioBtn from './WizardRadioBtn';
import Lottie from 'react-lottie';

import Swal from 'sweetalert2/dist/sweetalert2.js';
import {
    Row,
    Col,
    Image,
    Spinner,
} from 'react-bootstrap';

import {
    Link,
    withRouter
} from 'react-router-dom';

import animationData from '../../../assets/json/loading.json';
const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');

function WizardPages(props) {

    const { history } = props;

    const [selectedPage, setSelectedPage] = useState(null);
    const [showInstgram, setShowInstgram] = useState(false);
    const [showFacebook, setShowFacebook] = useState(false);
    const [loading, setLoading] = useState(true);
    const [btnLoading, setBtnLoading] = useState(false);
   
    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    useEffect(() => {
        // Getting insta & fb DATA :
        // if(props.fbData === null || props.instaData === null) {
        //     props.getFbData();
        //     props.getInstaData();
        // };
        props.getFbData();
        props.getInstaData();
        
        setTimeout(() => {
            setLoading(false);
        },2000);
    },[]);

    useEffect(() => {
        props.wizardIntentType === null && history.push('/home/wizard-popup');
    }, [props.wizardIntentType]);

    useEffect(() => {
        console.log('Hola', props.fbData['listPageNotConnected'], props.fbData);
    },[props.fbData]);
    
    const renderPage = (index,platform,pageName,picture,idPage,accessToken,pageStatus) => (
        <>
            <Row key={index} className="justify-content-between mb-4" onClick={() => { setSelectedPage(idPage); props.setWizardSelectedPage(platform,pageName,picture,idPage,accessToken,pageStatus) }} style={{ cursor: 'pointer' }}>
                <Row className="d-flex">
                    <div className="d-flex flex-column">
                        {
                        picture !== null 
                        ?
                            <Image src={picture} className="m-auto" style={{maxWidth: '40px'}} />
                        :
                            <Image src={`https://graph.facebook.com/v8.0/${idPage}/picture?access_token=${accessToken}`} className="m-auto" style={{maxWidth: '50px'}} />
                        }
                        <Image src={platform === "insta" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                    </div>

                    <p className="my-auto ml-3 wizard-pages-page-title">{pageName}</p>
                </Row>

                <div className="d-flex">
                    {/* <input type="checkbox" className="m-auto" /> */}
                    <WizardRadioBtn checked={selectedPage === idPage} />
                </div>
            </Row>

            <hr />
        </>
    );

    const checkNextStep = () => {
        if(selectedPage !== null && props.wizardIntentType !== null) {
            setBtnLoading(true);

            if( props.wizardIntentType === "generic"  ) {
                //Add Project 
                props.addProject(props.wizardSelectedPage)
                    .then(() =>{
                        // setBtnLoading(false);
                        props.nextSpecificWizardStep(2);
                    });
                // props.addProject(props.wizardSelectedPage)
                // props.nextSpecificWizardStep(2);
                    
            } else {
                props.nextWizardStep();
            }
        } else {
            const swalWithBootstrapButtons = Swal.mixin({
                customClass: {
                  confirmButton: 'wizard-pages-active-btn',
                },
                buttonsStyling: false
            });
            swalWithBootstrapButtons.fire({
                title: 'Please select a page',
                confirmButtonText: 'Okay',
                // text: "Please select a page",
                // icon: 'warning',
            });
        }
    };

    const handleFilter = (platform) => {
        if(platform === 'facebook') {
            setShowFacebook(true);
            setShowInstgram(false);
        } else if(platform === 'instagram') {
            setShowFacebook(false);
            setShowInstgram(true);
        } else {
            setShowFacebook(false);
            setShowInstgram(false);
        }
    };   

    return (
    <>
    <Row className="d-flex mt-5">  
        <Col lg={1} />

        <Col className="d-flex flex-column wizard-pages-container" style={{backgroundColor: 'white'}}>
            <Row className="d-flex mt-4 ml-2">
                <p className="my-auto wizard-pages-title">Select a page to moderate.</p>
            </Row>
            
            {/* FILTER BAR */}
            <Row className="mt-3 mb-3">
                <Col lg={2} />

                <Col className="wizard-pages-filter-bar">
                    <Row className="d-flex">
                        <div className={!showFacebook && !showInstgram ? "wizard-pages-filter-btn-active" : "wizard-pages-filter-btn"} onClick={() => handleFilter('all')}>
                            All pages
                        </div>
                        
                        <div className={showFacebook ? "wizard-pages-filter-btn-active" : "wizard-pages-filter-btn"} style={{ cursor: 'pointer' }} onClick={() => handleFilter('facebook')}>
                            Facebook
                        </div>
                        
                        <div className={showInstgram ? "wizard-pages-filter-btn-active" : "wizard-pages-filter-btn"} style={{ cursor: 'pointer' }} onClick={() => handleFilter('instagram')}>
                           Instagram
                        </div>
                    </Row>
                </Col>

                <Col lg={2} />
            </Row>


            {
            !loading
            ?
                (
                    <Row className="mt-5 mb-5">
                        <Col lg={2} />


                        {!showInstgram && !showFacebook &&
                            <Col className="pl-5 pr-4">
                                <>
                                {props.fbData !== null && 
                                    Object.keys(props.fbData).map( (key, index) =>
                                        key === "listPageInvite" 
                                        &&
                                        props.fbData["listPageInvite"].map((page,index) => (
                                            renderPage(index,"fb",page.name,null,page.idPage,page.access_token,key)   
                                        ))
                                    )
                                }

                                
                                {props.instaData !== null &&
                                    Object.keys(props.instaData).map( (key, index) =>
                                        key === "listPageInvite"
                                        &&
                                        props.instaData[key].map((page,index) => (
                                            renderPage(index,"insta",page.name,page.imageUrl,page.idPage,page.access_token,key)   
                                        ))
                                    )
                                }
                                </>
                            </Col> 
                        }

                        {showInstgram &&
                            <Col className="pl-5 pr-4">
                                <> 
                                {props.instaData !== null &&
                                    Object.keys(props.instaData).map( (key, index) =>
                                        props.instaData[key].map((page,index) => (
                                            renderPage(index,"insta",page.name,page.imageUrl,page.idPage,page.access_token)   
                                        ))
                                    )
                                }
                                </>
                            </Col> 
                        }

                        {showFacebook &&
                            <Col className="pl-5 pr-4">
                                {props.fbData !== null && 
                                    Object.keys(props.fbData).map( (key, index) =>
                                        props.fbData[key].map((page,index) => (
                                            renderPage(index,"fb",page.name,null,page.idPage,page.access_token)   
                                        ))
                                    )
                                }
                            </Col> 
                        }

                        <Col lg={2} />
                    </Row>
                )
            :
                (
                    <div className="d-flex" style={{ width: '100%' }}>
                        <div className="m-auto">
                            <Lottie options={defaultOptions} width={200} /*height={400}*/ />
                        </div>
                    </div>
                )
            }
           
            <Row className="d-flex justify-content-between pl-4 pr-4">
                <div className="wizard-pages-inactive-btn mb-3">
                    {/* MUST RESET ALL WIZARD VARS */}
                    <Link to="/home/pages" style={{textDecoration: 'none', color: '#818E94'}} onClick={() => props.resetWizardStep()} >
                        Back
                    </Link>
                </div>

                <div className="mb-3">
                    <Row>
                        <div className="wizard-pages-inactive-btn mr-3">
                            Save Draft
                        </div>

                        <div className="wizard-pages-active-btn" onClick={() => checkNextStep() }>
                            {
                                btnLoading
                                ?
                                    (<Spinner animation="border" variant="light" />)
                                :
                                    "Next"
                            }
                        </div>
                    </Row>
                </div>
            </Row>

        </Col>

        <Col lg={1} />
    </Row>
    </>
    );
}


