pages: (7) [{…}, {…}, {…}, {…}, {…}, {…}, {…}]
parent_id: []
profilePictureUrl: "https://scontent.ftun5-1.fna.fbcdn.net/v/t1.30497-1/c212.0.720.720a/p720x720/84628273_176159830277856_972693363922829312_n.jpg?_nc_cat=1&_nc_sid=12b3be&_nc_ohc=4RSf-Cvnc8kAX_I4ilU&_nc_oc=AQm-zePN4JNrOCo9VrDGlK9-EYUNBXpqIbJ0qqdC3FOHTIc3jD0FzdCXyvMSCyeIUcA&_nc_ht=scontent.ftun5-1.fna&oh=8667c2565c91557a63e677f37e1216d6&oe=5F4D909F"
projects: (14) [{…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}]

*/

function Navbar(props) {
    
    let {
        location
    } = props;

    const [userDataFromLogin, setUserDataFromLogin] = useState(null);
    const [redirect, setRedirect] = useState(false);
    
    useEffect(() => {
        let userDataFromLocal = JSON.parse(localStorage.getItem('userData'));
        setUserDataFromLogin(userDataFromLocal);
    },[]);

    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a href="" ref={ref} onClick={(e) => { e.preventDefault(); onClick(e); }} >
          <IoIosArrowDown color={"#818E94"} size={"17"} style={{ marginBottom: '50px!important' }} className="my-auto ml-2 navbar-arrow-icon" />
        </a>
    ));

    const Logout = async () => {
        
        setUserDataFromLogin(null);

        await axios.post(host + '/api/v1/auth/logout', null, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        }).then(async (res) => {
            console.log("Rep logout", res.data);
            await localStorage.clear();
            setTimeout(() => {
                setRedirect(true);
            },1000);
        })
        .catch(err => {
            console.log("Cannot logout", err);
        });

    };

    /* When the user clicks on the button, 
    toggle between hiding and showing the dropdown content */
    function myFunction() {
        document.getElementById("myDropdown").classList.toggle("show");
    }
    
    // Close the dropdown if the user clicks outside of it
    window.onclick = function(event) {
        if (!event.target.matches('.navbar-menu-title')) {
        var dropdowns = document.getElementsByClassName("dropdown-content");
        var i;
        for (i = 0; i < dropdowns.length; i++) {
            var openDropdown = dropdowns[i];
            if (openDropdown.classList.contains('show')) {
            openDropdown.classList.remove('show');
            }
        }
        }
    }
    
    return (
        <>
        { redirect && <Redirect to={"/"} /> }
        
        {location.pathname === "/" 
        &&
        <Row className="justify-content-center free-trial p-3">
             <Col lg={2} className="d-flex">
                <div className="text-free-trial m-auto" >Start your free trial now</div>
             </Col>
             <Col lg={2} className="d-flex ">
                    {/* <a href={menuItem.route}  className="button-free-trial mr-auto my-auto d-flex">
                        <div className="m-auto text-button-free-trial">Sign up</div> 
                    </a> */}
                 {/* <button  type="button" className="button-free-trial mr-auto my-auto d-flex"> */}
                    <div className="mr-auto my-auto d-flex"> 
                        <Facebook type="signUp" content="Sign up" />
                    </div>
                 {/* </button> */}
             </Col>
        </Row>
        }

        <Nav
            activeKey="/home"
            onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
            // className="justify-content-between mx-0 pt-0"
            style={{ display: 'flex',
            position: 'relative',
            zIndex: '1'
            }} >

            <Row className="justify-content-center m-auto w-100 navbar-menu-style py-2"  style={{
                // background: 'var(--unnamed-color-ffffff) 0% 0% no-repeat padding-box',
                // background:' #FFFFFF 0% 0% no-repeat padding-box',
                boxShadow: '0px 3px 6px #00000029',
                position: 'fixed',
                zIndex: '2'
                // opacity: '1',
                // box-shadow: 0px 3px 6px #00000029;
            }}> 
                <Col lg={6}>
                    <Row>
                        <Col className="d-flex p-2" lg={4}>
                            <Image src={LOGO} className={"my-auto mr-auto ml-5"} style={{ maxWidth: '80px' }} />
                        </Col>

                        <Col lg={8}  className="d-flex">
                            <Row className={"mt-auto"}>
                            {/* <Row className={"mt-auto mr-auto p-2"}> */}
                                {
                                    props.menuItems.length > 0 
                                    && 
                                    props.menuItems.map(((menuItem,index) => 
                                        <Nav.Item key={index}>
                                            <Nav.Link href={menuItem.route} className="navbar-menu-title">{menuItem.title}</Nav.Link>
                                        </Nav.Item>
                                    ))
                                }
                            </Row>
                        </Col>
                    </Row>
                </Col>

                <Col lg={4} className="d-flex">
                    {/* <Row className="d-flex"> */}
                    {/* <Row className={"mt-auto ml-auto p-3"}> */}
                        {
                            props.loginBtn
                            ?
                            <Nav.Item className="ml-auto mt-auto mr-5" style={{
                                padding: '.5rem 1rem'
                            }}>
                                <Facebook type="nav" content="Login" />
                            </Nav.Item>
                            :
                            userDataFromLogin && !props.loginBtn
                            ?
                            // <Nav.Item className="ml-auto mt-auto">
                            //     <Row className="d-flex">
                            //         <Image src={userDataFromLogin['user'].profilePictureUrl} style={{ maxWidth: '50px', borderRadius: '50%' }} className={"my-auto"} />
                            //         <Dropdown as={NavItem} >
                            //             <Dropdown.Toggle  as={CustomToggle} id="dropdown-custom-components" style={{top: '31px',borderRadius: '0',backgroundColor: 'silver'}} />

                            //             <Dropdown.Menu style={{top: '31px!important',borderRadius: '0',backgroundColor: 'gold'}}>
                            //                 <Dropdown.Item onClick={() => Logout()} className="navbar-menu-title">Logout</Dropdown.Item>
                            //             </Dropdown.Menu>
                            //         </Dropdown>
                            //     </Row>
                            // </Nav.Item>
                            <div className="ml-auto mt-auto" >
                                <div className="dropdown">
                                    {/* <a  onClick={() => myFunction()} class="navbar-menu-title">Dropdown</a><br/> */}
                                    {/* <div  onClick={() => myFunction()}  id="dropdown-custom-components" className="navbar-menu-title" > */}
                                    <div >
                                        <Row className="">
                                            <Col xs={6} className="d-flex" ><Image src={userDataFromLogin['user'].profilePictureUrl} style={{ maxWidth: '50px', borderRadius: '50%',userSelect: 'none' }} className={"my-auto"} /></Col>
                                            <Col xs={6} className="d-flex navbar-menu-title">
                                                    <IoIosArrowDown className="navbar-menu-title navbar-arrow-icon "  onClick={() => myFunction()}  id="dropdown-custom-components" color={"#818E94"} size={"17"} />
                                            </Col>
                                        </Row>
                                        
                                    </div>

                                    <div id="myDropdown" className="dropdown-content">
                                        {/* <Row>
                                            <Col xs={6} className="d-flex"><LogoutSvg className="m-auto"/></Col>
                                            <Col xs={6} className="d-flex"><a className="m-auto navbar-menu-title" onClick={() => Logout()} >Logout</a></Col>
                                        </Row> */}
                                        <div className="d-flex" >

                                            <LogoutSvg className="m-auto" style={{alignItems: 'flex-start'}} />
                                            <a className="m-auto navbar-menu-title" onClick={() => Logout()} style={{alignItems: 'flex-end'}} >Logout</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            :
                            <div/>
                            // <Spinner animation="border" size="sm" variant="dark" />
                        }
                    {/* </Row> */}
                </Col>
            
            {/* <Col lg={1} /> */}
</Row>
        </Nav>

        </>
    );
}


export default withRouter(Navbar);
