import React from 'react';
import './App.css';
import { FacebookProvider, LoginButton } from 'react-facebook';
import axios from 'axios'
function Facebook() {
  const handleResponse = (data) => {
    axios.defaults.headers['Authorization'] = 'Bearer ' + JSON.stringify(JSON.stringify(data.tokenDetail.accessToken))
    axios.post('https://node3.mobilesupportbot.com:5075/api/v1/auth/login/facebook', {
      token: JSON.stringify(data.tokenDetail.accessToken)
    }).then(res => res.json())
      .then(resdata => console.log(resdata))
      .catch(err => console.log('errbb ', err))
    console.log(data)
  }

  const handleError = (error) => {
    console.log(error);
  }

  return (
    <div className="App">
      <button onClick={handleResponse}>click me</button>
      <FacebookProvider appId="783983335073350">
        <LoginButton
          scope="public_profile,publish_pages,email,pages_messaging,pages_manage_cta,pages_show_list,manage_pages"
          onCompleted={handleResponse}
          onError={handleError}
          className="test"
        >
          <span>Login via Facebook</span>
        </LoginButton>
      </FacebookProvider>
    </div>
  );
}

export default Facebook;
