import React, { useEffect } from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    // Redirect,
} from "react-router-dom";

import Home from './app/Home/Home';
import LandingPage from './app/Login/LandingPage';


function App() {
    
    useEffect(() => {
        const scriptJquery = document.createElement('script');
        const scriptLightSlider = document.createElement('script');
        const scriptCustom = document.createElement('script');
      
        scriptJquery.src = "./src/assets/carousel/js/JQuery3.3.1.js";
        scriptLightSlider.src = "./src/assets/carousel/js/lightslider.js";
        scriptCustom.src = "./src/assets/carousel/js/script.js";

        scriptJquery.async = true;
        scriptLightSlider.async = true;
        scriptCustom.async = true;
      
        document.body.appendChild(scriptJquery);
        document.body.appendChild(scriptLightSlider);
        document.body.appendChild(scriptCustom);
      
        return () => {
          document.body.removeChild(scriptJquery);
          document.body.removeChild(scriptLightSlider);
          document.body.removeChild(scriptCustom);
        }
    }, []);

    return (
        <Router>
            <Switch>
                <Route path="/home">
                    <Home />
                </Route>
                
                <Route path="/">
                    <LandingPage />
                </Route>
            
            </Switch>
        </Router>
    );
}

export default App;
