import React from 'react'

function comment(props) {
    return (
        <div style={{
            background: props.bg ? props.bg : "#fff",
            padding: 20,
            borderBottom: '1px solid lightgray',
            cursor: 'pointer'
        }} onClick={() => props.handelClick ? props.handelClick(props.comment) : console.log('already matched')}>
            <div style={{ display: 'flex', justifyContent: 'space-around', alignItems: 'center' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img src={`https://graph.facebook.com/v3.2/${props.comment.publisher.id}/picture`} style={{ width: 40, height: 40, borderRadius: '50%' }} />
                    <span style={{ marginLeft: 10, fontFamily: "poppins", fontSize: 14, color: "#818E94", fontWeight: "bolder" }}>{props.comment.publisher.name}</span>
                </div>
                <div style={{ fontSize: 14, color: "#B4B4B4", fontFamily: 'poppins' }}>{new Date(props.comment.created_time).toLocaleDateString()} {new Date(props.comment.created_time).toLocaleTimeString()}</div>
            </div>
            <div style={{ paddingLeft: 70, fontFamily: 'Poppins', fontSize: 14, marginTop: 13 }}>{props.comment.message}</div>
            <div style={{ margin: "30px 0 0 20px", color: "#818E94", fontSize: 14, fontFamily: 'poppins' }}>Intent: {props.comment.nlp_rep.intent}</div>
            {!props.comment.success && <input type="checkbox" checked={props.comment.treated} style={{
                float: "right",
                transform: "translateY(-14px)"
            }} />}
        </div>
    )
}

export default comment
