import React, { useState, useRef, useEffect } from 'react';
import { connect } from 'react-redux';

import { Row, } from 'react-bootstrap';

import { MdAddCircleOutline } from 'react-icons/md';
import Swal from 'sweetalert2/dist/sweetalert2.js';

export const AddSynonymInput = (props) => {

    let {
        product,
        indexSyn,
        categorySelected,
        socialMediaPageSelected,

        // Functions
        addSynonym,
        removeSynonym,
    } = props;

    const inputRef = useRef();
    const [enableSynonymInput,setEnableSynonymInput] = useState(false);
    const [Synonym,setSynonym] = useState('');

    useEffect(() => {
        enableSynonymInput && inputRef.current.focus();
    },[enableSynonymInput]);


    const newSynonym = () => {
        
        addSynonym(Synonym, categorySelected, product, indexSyn, socialMediaPageSelected.id)
        .then(() => {
            setSynonym(''); 
            setEnableSynonymInput(false); 
            props.setSynonymBoxBorder(false); 
        }) 
        .catch(() => {
            Swal.fire({
                position: 'center',
                icon: 'error',
                title: 'Error while adding the synonym',
                showConfirmButton: false,
                timer: 2000
            })
            setSynonym(''); 
            setEnableSynonymInput(false); 
            props.setSynonymBoxBorder(false); 
        });
    };

    const deleteTheSynonym = () => {
        // removeSynonym(synonymValue, categorySelected, product, indexProduct, idPage) 
        removeSynonym(synonymValue, categorySelected, product, indexProduct, idPage) 
        .catch(() => {
            Swal.fire({
                position: 'center',
                icon: 'error',
                title: 'Error while deleting the synonym',
                showConfirmButton: false,
                timer: 2000
            });
        });
    };

    return (
        <div>
            {
            enableSynonymInput
            ?
            <Row className="d-flex mx-auto" style={{ cursor: 'pointer' }}>
                <input 
                    type="text" 
                    className="add-new-product-input w-100" 
                    ref={inputRef} 
                    value={Synonym} 
                    onChange={(e) => setSynonym(e.target.value)} 
                    onKeyDown={(e) => e.key === 'Enter' && newSynonym()} 
                />
            </Row>
            : 
            <Row className="d-flex mx-auto" style={{ cursor: 'pointer' }} onClick={() => { setEnableSynonymInput(true); }}>
                <MdAddCircleOutline className="my-auto mr-1" color={'#818E94'} size={'17'} onClick={deleteTheSynonym}  />
                <p className="my-auto">Add Synonym</p>
            </Row>
            }
        </div>
    )
}

const mapStateToProps = (state) => ({
    socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    categorySelected : state.productsR.categorySelected,
    productSelected: state.productsR.productSelected, 
})

export default connect(mapStateToProps,{})(AddSynonymInput)
