import React, {useState, useRef} from 'react';


import {
    Row,
    Col,
    Image,
    OverlayTrigger,
    Overlay,
    Popover,
    Form,
} from 'react-bootstrap';
import Picker from 'emoji-picker-react';
import Keyboard from 'react-simple-keyboard';
import layout from "simple-keyboard-layouts/build/layouts/arabic";
import ReactTooltip from 'react-tooltip';
import Swal from 'sweetalert2/dist/sweetalert2.js';


import { IoIosCall } from 'react-icons/io';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import { BsLink45Deg } from 'react-icons/bs';

import {ReactComponent as Arabic} from '../../../assets/images/home/Arabic.svg';
import {ReactComponent as ArabicInverse} from '../../../assets/images/home/arabic01.svg';
import {ReactComponent as Emoticon} from '../../../assets/images/home/emoticon.svg';
import {ReactComponent as EmoticonInverse} from '../../../assets/images/home/emoticon01.svg';
const Tag = require('../../../assets/images/home/Tag.svg');
const TagInverse = require('../../../assets/images/home/tag01.svg');

// OLD Icons:
// const BALISE_ICON = require('../../../assets/images/home/balise.png');
// const BALISE_ICON_ACTIVE = require('../../../assets/images/home/balise_active.png');
// const EMO_ICON = require('../../../assets/images/home/emo.png');
// const EMO_ICON_ACTIVE = require('../../../assets/images/home/emo_active.png');
// const LETTER_ICON = require('../../../assets/images/home/3a.png');
// const LETTER_ICON_ACTIVE = require('../../../assets/images/home/3a_active.png');


export default function WizardMiracleInput(props) {

    const [BtnPop, setBtnPop] = useState(false);

    const [BtnUpdatePop, setBtnUpdatePop] = useState(false);
    const [BtnUpdatePopTarget, setBtnUpdatePopTarget] = useState(null);
    const BtnUpdateRef = useRef(null);
    const [BtnUpdateIndex, setBtnUpdateIndex] = useState(null);
    const [BtnUpdateType, setBtnUpdateType] = useState('');
    const [BtnUpdateTitle, setBtnUpdateTitle] = useState('');
    const [BtnUpdateValue, setBtnUpdateValue] = useState('');
   
    let {
        label,
        response,
        handleResponse,
        showTags,
        handleTags,
        showEmojis,
        handleEmojis,
        showKeyboard,
        handleKeyboard,
        BtnType,
        handleBtnType,
        BtnTitle,
        handleBtnTitle,
        BtnValue,
        handleBtnValue,
        Btns,
        handleBtns,
        platform,
    } = props;

    // Popup Config :
    const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
          confirmButton: 'wizard-pages-active-btn py-3 px-3',
        },
        buttonsStyling: false
    });

    // render Functions :
    const Tags = (props) => {
        console.log("hola",props);
    return    (
            <Popover id="popover-tags" style={{ borderStyle: "hidden!important" }}>
              <Popover.Content style={{ borderStyle: "hidden!important" }}>
                <Row>
                    <Col lg={12}>
                        {/* <Row className="d-flex w-100">
                            <IoIosClose className="ml-auto my-auto pt-1" color={'#9F9F9F'} size={30} onClick={() => { handleTags(false) }} style={{cursor: 'pointer'}} />
                        </Row> */}
    
                        <Row className="pl-1 my-2">
                            <Col xs={12} className="d-flex mb-1 mt-2">
                                <p className="my-auto mr-auto wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##FIRST_NAME##'); }}>First Name</p>
                            </Col>
                             
                            <Col xs={12} className="d-flex mb-1">
                                <p className="my-auto mr-auto wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##LAST_NAME##'); }}>Last Name</p>
                            </Col>
    
                            <Col xs={12} className="d-flex mb-1">
                                <p className="my-auto mr-auto wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##FULL_NAME##'); }}  >Full Name</p>
                            </Col>
    
                            <Col xs={12} className="m-auto d-flex mb-2">
                                <p className="my-auto mr-auto wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##PAGE_NAME##'); }}  >Page Name</p>
                            </Col> 
                        </Row>
                    </Col>
                </Row>
              </Popover.Content>
            </Popover>
        );
    } 

    const BtnsPopover = (
        <Popover id="popover-quick-replies">
          <Popover.Content>
            <Row className="justify-content-center">
                <Col md={12}>
                    <Row>
                        <Col md={12} className="mt-2 mb-3">
                            <Form.Control as="select" onChange={(e) => handleBtnType(e.target.value)} defaultValue={BtnType}>
                                <option value="web_url">URL</option>
                                <option value="phone_number">CALL</option>
                            </Form.Control>
                        </Col>
                        
                        <Col md={12} className="mt-2">
                            <Row className="d-flex">
                                <Col md={12}>
                                    <Form.Control 
                                        type={"text"} 
                                        onChange={(e) => handleBtnTitle(e.target.value)} 
                                        value={BtnTitle}  
                                        placeholder="Title"
                                        maxLength="20" 
                                    />
                                </Col>

                                <Col md={12} className="d-flex">
                                    <p className="ml-auto wizard-config-input-feedback">Remaining characters: {20 - BtnTitle.length}</p>
                                </Col>
                            </Row>
                        </Col>

                        <Col md={12} className="mb-2">
                            <Form.Control 
                                type={BtnType === "web_url" ? "text" : "number"} 
                                onChange={(e) => handleBtnValue(e.target.value)} 
                                value={BtnValue} 
                                placeholder={BtnType === "web_url" ? "www.example.com" : "(+216) 99 999 999"} 
                            />
                        </Col>
                    </Row>
                </Col>

                <Col md={11} className="d-flex pl-1">
                    <div className="wizard-pages-active-btn py-2 px-4" onClick={() => addNewBtn()}>
                        OK
                    </div>
                </Col>   
            </Row>
                
          </Popover.Content>
        </Popover>
    );

    const BtnsUpdatePopover = () => (
        <Popover id="popover-quick-replies">
          <Popover.Content>
            <Row className="justify-content-center">
                <Col md={12}>
                    <Row>
                        <Col md={12} className="mt-2 mb-3">
                            <Form.Control as="select" onChange={(e) => setBtnUpdateType(e.target.value)} defaultValue={BtnUpdateType}>
                                <option value="web_url">URL</option>
                                <option value="phone_number">CALL</option>
                            </Form.Control>
                        </Col>
                        
                        <Col md={12} className="mt-2">
                            <Row className="d-flex">
                                <Col md={12}>
                                    <Form.Control 
                                        type={"text"} 
                                        onChange={(e) => setBtnUpdateTitle(e.target.value)} 
                                        value={BtnUpdateTitle}  
                                        placeholder="Title" 
                                        maxLength="20"
                                    />
                                </Col>

                                <Col md={12} className="d-flex">
                                    <p className="ml-auto wizard-config-input-feedback">Remaining characters: {20 - BtnUpdateTitle.length}</p>
                                </Col>
                            </Row>
                        </Col>

                        <Col md={12} className="mb-2">
                            <Form.Control 
                                type={BtnType === "web_url" ? "text" : "number"} 
                                onChange={(e) => setBtnUpdateValue(e.target.value)} 
                                value={BtnUpdateValue} 
                                placeholder={BtnUpdateType === "web_url" ? "www.example.com" : "(+216) 99 999 999"} 
                            />
                        </Col>
                    </Row>
                </Col>

                <Col md={11} className="d-flex pl-1">
                    <div className="wizard-pages-active-btn py-2 px-4" onClick={() => updateBtn()}>
                       OK
                    </div>
                </Col>   
            </Row>
                
          </Popover.Content>
        </Popover>
    );

    const popup = (message) => ( swalWithBootstrapButtons.fire({ title: `${message}`,confirmButtonText: 'Okay' }) );

    // Functions :
    const addNewBtn = () => {
        let newBtns = [...Btns];
    
        if(BtnType === "web_url") {
            let objectFb = {
                type: "web_url",
                url: BtnValue,
                title: BtnTitle,
                webview_height_ratio: "tall",
            } 

            if(BtnTitle === "") {
                popup("Button title is empty !");
            } else if (BtnValue === "") {
                popup("Button url is empty!");
            } else {
                newBtns.push(objectFb);
                // update new Btns :
                handleBtns(newBtns);
                // reset
                resetBtns();
            }
        } else {
            let objectFb = {
                type: "phone_number",
                title: BtnTitle,
                payload: BtnValue,
            };

            if(BtnTitle === "") {
                popup("Button title is empty !");
            } else if (BtnValue === "") {
                popup("Button phone number is empty!");
            } else {
                newBtns.push(objectFb);
                // update new Btns :
                handleBtns(newBtns);
                // reset
                resetBtns();
            }
        }

    };

    const updateBtn = () => {
        let newBtns = [...Btns];
        let newType = [...BtnUpdateType].join('');
        let newTitle = [...BtnUpdateTitle].join('');
        let newValue = [...BtnUpdateValue].join('');

        if(newType === "web_url") {
            newBtns[BtnUpdateIndex].type = BtnUpdateType;
            newBtns[BtnUpdateIndex].title = newTitle;
            newBtns[BtnUpdateIndex].url = newValue;
            handleBtns(newBtns);
            setBtnUpdatePop(false);
        } else {
            newBtns[BtnUpdateIndex].type = BtnUpdateType;
            newBtns[BtnUpdateIndex].title = newTitle;
            newBtns[BtnUpdateIndex].payload = newValue;
            handleBtns(newBtns);
            setBtnUpdatePop(false);
        };
    };

    const deleteBtn = (index) => {
        let newBtns = [...Btns];
        newBtns.splice(index,1);
        handleBtns(newBtns);
    };

    const resetBtns = () => {
        handleBtnType('web_url');
        handleBtnTitle('');
        handleBtnValue('');
        setBtnPop(false);
        // setBtnUpdatePop(false);
    };

    const handleUpdatePopover = (e,btn,indexBtn) => {
        setBtnUpdatePopTarget(e.target);
        setBtnPop(false);
        setBtnUpdatePop(false);

        if(btn.type === "web_url") {
            setBtnUpdateType("web_url");
            setBtnUpdateIndex(indexBtn);
            setBtnUpdateTitle(btn.title);
            setBtnUpdateValue(btn.url);
            setBtnUpdatePop(true);
        } else {
            setBtnUpdateType("phone_number");
            setBtnUpdateIndex(indexBtn);
            setBtnUpdateTitle(btn.title);
            setBtnUpdateValue(btn.payload);
            setBtnUpdatePop(true);
        }
    };
    
    return (
        <Row className="mx-0 px-2">

            <Col lg={12} className="mx-0 px-2 d-flex">
                <label className="wizard-config-textarea-label my-2">{label}</label>
            </Col>

            <Col lg={12} className="px-2 mx-0">
            <div className="wizard-intent-miracle-box">
                <Row className="mb-1 mx-0 pt-1 px-2">
                    <Col lg={1} className="d-flex p-0">
                        <OverlayTrigger trigger="click" placement="top" overlay={Tags}>
                            <Image 
                                src={showTags ? TagInverse : Tag} 
                                onClick={() => handleTags(!showTags)} 
                                className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                                style={{ width: '19px', cursor: 'pointer'}} 
                            />   
                            {/* {showTags ?  
                            <TagInverse   
                                width={20} 
                                height={20} 
                                style={{cursor: 'pointer'}} 
                                onClick={() => handleTags(false)} 
                                className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            />
                            :    
                            <Tag 
                                width={20} 
                                height={20} 
                                style={{cursor: 'pointer'}} 
                                onClick={() => handleTags(true)} 
                                className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            />
                            } */}
                           

                         
                        </OverlayTrigger> 
                    </Col>                                

                    <Col lg={1} className="d-flex p-0">
                        {/* <Image 
                            src={showEmojis ? EMO_ICON_ACTIVE : EMO_ICON} 
                            onClick={() => handleEmojis(!showEmojis)} 
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '18px', cursor: 'pointer'}} 
                        /> */}

                        {showEmojis ? 
                        <EmoticonInverse   
                            width={20} 
                            height={20} 
                            style={{cursor: 'pointer'}} 
                            onClick={() => handleEmojis(!showEmojis)}  
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                        />
                        : 
                        <Emoticon 
                            width={20} 
                            height={20} 
                            style={{cursor: 'pointer'}} 
                            onClick={() => handleEmojis(!showEmojis)}  
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                        />
                        }
                    </Col>
        
                    <Col lg={1} className="d-flex p-0">
                        {/* <Image 
                            src={showKeyboard ? LETTER_ICON_ACTIVE : LETTER_ICON} 
                            onClick={() => handleKeyboard(!showKeyboard)} 
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '21px', cursor: 'pointer'}} 
                        /> */}
                        {showKeyboard ? 
                         
                        <ArabicInverse   
                            width={20} 
                            height={20} 
                            style={{cursor: 'pointer'}} 
                            onClick={() => handleKeyboard(!showKeyboard)}  
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                        />
                        : 

                        <Arabic  
                        width={20} 
                        height={20} 
                        style={{cursor: 'pointer'}} 
                        onClick={() => handleKeyboard(!showKeyboard)}  
                        className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                        />
                        }

                    </Col>
                
                    <Col lg={8}>
                        {showKeyboard &&
                            <div className="wizard-config-keyboard-container">
                                <Keyboard 
                                    onChange={(input) => handleResponse(prevResponse => prevResponse + input.charAt(input.length - 1))} 
                                    // onKeyPress={(button) => onKeyPress(button,"generic")} 
                                    layout={layout} 
                                />
                            </div>
                        }

                        {showEmojis &&
                            <div className="wizard-config-emoji-container" >
                                <Picker onEmojiClick={(e,obj) => handleResponse(prevResponse=> prevResponse + obj.emoji) } />
                            </div>
                        } 

                    </Col>
                </Row>

                <Row>
                    <Col lg={12} className="mx-0">
                        <textarea 
                            onChange={(e) => handleResponse(e.target.value)} 
                            placeholder={"Your Message..."}
                            // onFocus={() => console.log("Hey I'm Focused")} 
                            value={response} 
                            className="wizard-config-textarea w-100 py-0" 
                            rows="5"
                            maxLength="640"
                        />
                    </Col>
                </Row>

                <Row >
                    <Col lg={12} className="my-0 py-0">
                        <hr style={{height: '2px', borderColor: '#B4B4B4', margin: '1px'}} />
                    </Col>
                </Row>

                {platform === "facebook"
                &&
                <Row className="mx-0">
                    <Col lg={12}>
                        <Row>
                            {Btns.map((btn,indexBtn) => 
                                <Col key={indexBtn} lg={3} className="mx-1">
                                    <Row className="wizard-config-quick-replies-btn my-1 py-1 d-flex">
                                        {btn.type === "web_url" 
                                        ? 
                                        <BsLink45Deg color={'#fff'} size={15} className="ml-1 mr-auto my-auto" onClick={(e) => handleUpdatePopover(e,btn,indexBtn)}/>
                                        :
                                        <IoIosCall color={'#fff'} size={15} className="ml-1 mr-auto my-auto" onClick={(e) => handleUpdatePopover(e,btn,indexBtn) }/>
                                        }
                                        <p className={btn.type === "web_url" ? "mr-auto my-auto" : "ml-1 mr-auto my-auto" } data-for='quickReplyTip' data-tip={btn.title} onClick={(e) => {handleUpdatePopover(e,btn,indexBtn)}}>{btn.type === "web_url" ? "url" : "call"}</p>
                                        <AiOutlineCloseCircle className="ml-auto my-auto mr-1" color={"white"} size={"15"} style={{cursor: 'pointer'}} onClick={() => deleteBtn(indexBtn)} />
                                    </Row>
                                    <ReactTooltip id='quickReplyTip' textColor='#fff' backgroundColor='#009EE3' />
                                </Col>
                            )}
                            {Btns.length < 3
                            &&
                                    
                            <OverlayTrigger trigger="click" placement="right" show={BtnPop} overlay={BtnsPopover} >
                                <Col lg={3} className="wizard-config-quick-replies-btn m-1 py-1 d-flex px-0" onClick={() => {setBtnUpdatePop(false); setBtnPop(prevState => prevState ? false : true);}}>
                                    <p className="m-auto">Add button</p>
                                </Col>
                            </OverlayTrigger>
                            }

                            <div ref={BtnUpdateRef}>
                                <Overlay
                                    show={BtnUpdatePop}
                                    target={BtnUpdatePopTarget}
                                    placement="right"
                                    container={BtnUpdateRef.current}
                                    
                                >
                                   {BtnsUpdatePopover()}
                                </Overlay>
                            </div>

                        </Row>
                    </Col>
                </Row> 
                }
               </div>
            </Col>
        </Row>
    )
}
