import axios from 'axios'
import { host, trainagenthost } from '../../config';


export const getFbData = () => {
    return async (dispatch) => {
        
        let token = await localStorage.getItem('authtoken');

        return axios.get(host + '/api/v1/secure/pages/get/all/', {
            headers: {
                'authorization': 'Bearer ' + token
            }
        })
        .then(res => {
            // {Object.keys(res.data.data).map( (key, index) => 
            //     console.log("Yaaaa nadher ",key," => ",res.data.data[key])
            // )}

            dispatch({
                type: 'GET_FB_DATA',
                payload: res.data.data
            })
        }).catch((err) => console.log("GetFbData err", JSON.stringify(err)))
    }
};

export const getInstaData = () => {
    return async (dispatch) => {
        
        return axios.get(host + '/api/v1/secure/pagesInsta/get/all/', {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then(res => {
            // console.log('Instagram pages ya haider =>', res.data)

            dispatch({
                type: 'GET_INSTA_DATA',
                payload: res.data.data
            })
      

        }).catch((err) => {
            // console.log("Api instagram error", JSON.stringify(err))
        })
    }
};
