import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { resetSocialMediaSelections, inviteMember, deleteTeamMember, sendMailTeam, getTeamMembers, getAllPageConnected } from '../../../redux/actions/socialMediaActions';
import { resetَAllWizard, } from '../../../redux/actions/wizardActions';

import Pages from '../Pages/Pages';

import {
    Col,
    Row,
    Modal,
    Spinner,
    Image,
    Form,
    Button
} from 'react-bootstrap';

import Swal from 'sweetalert2/dist/sweetalert2.js';
import Lottie from 'react-lottie';

import { CopyToClipboard } from 'react-copy-to-clipboard';

import notFoundAnimationData from '../../../assets/json/not-found.json';
import { FaInfoCircle } from 'react-icons/fa';
import { IoIosCopy } from 'react-icons/io';
import {ReactComponent as Zoom} from '../../../assets/images/home/zoom-2.svg';
import {ReactComponent as Trash} from '../../../assets/images/home/trash-simple.svg';
import {ReactComponent as TrashOwner} from '../../../assets/images/home/trash-owner.svg';
// import {ReactComponent as Left} from '../../../assets/images/home/left.svg';
// import {ReactComponent as Right} from '../../../assets/images/home/right.svg';

const GRAY_SEPERATOR = require('../../../assets/images/home/gray-separator.png');

export const Team = (props) => {

    let {
        socialMediaPageSelected,
        teamMembers,
        
        // Funtions:
        getTeamMembers,
        sendMailTeam,
        inviteMember,
        deleteTeamMember,
        resetَAllWizard,
        resetSocialMediaSelections,
        getAllPageConnected,
    } = props;

    // const fbHost = "https://graph.facebook.com/v8.0";

    // Popup Config: 
    const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
        confirmButton: 'wizard-pages-active-btn',
        },
        buttonsStyling: false
    });

    // Lottie config
    const defaultOptionsNotFound = {
        loop: true,
        autoplay: true, 
        animationData: notFoundAnimationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };
   
    const [showPopover, setShowPopover] = useState(false);
    const [showInviteModal, setShowInviteModal] = useState(false);
    const [loadingInvite, setLoadingInvite] = useState(false);
    const [inviteToken, setInviteToken] = useState('');
    const [emailTeamMember,setEmailTeamMember] = useState(''); 
    const [nameTeamMember,setNameTeamMember] = useState(''); 
    // const [teamMembers, setTeamMembers] = useState([]);

    const handleInviteModal = () => setShowInviteModal(!showInviteModal);

    const handlePopover = (delay) => {
        setTimeout( () => {
            setShowPopover(!showPopover);
        },delay);
    };

    const validateEmail = (mail) => {
        if (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(mail)) {
            return (true);
        } else {
            return (false);
        }
    };
    
    const renderPopover = () => (
        <div className="d-flex manage-products-popover" >
            <p className="m-auto">
                You can invite other persons to join the team of a page so they can create and modify agents/products…<br/>
                <a href="">learn more</a>          
            </p>
        </div>
    );

    const renderModal = (page) => (
        <Modal show={showInviteModal} onHide={handleInviteModal} size="lg" centered>
            <div className="d-flex flex-column">
                {/* <Modal.Header closeButton> */}
                {/* <div closeButton> */}
                <Row className="">
                    <Col lg={6}><Modal.Title className="px-2 team-name py-3 ml-3">Add Member</Modal.Title></Col>
                    <Col lg={6} className="d-flex"><button  type="button" class="team-close ml-auto mr-3" onClick={() => setShowInviteModal(false)} ><span aria-hidden="true">×</span></button></Col>
                    
                    
                    <hr  style={{borderBottom: '1px solid #818E94', opacity: '0.1',padding: '0',margin: '0px auto',width:'90%'}} />
                </Row>
                {/* </div> */}
                {/* </Modal.Header> */}

                {/* <IoIosClose className="ml-auto mr-3 mt-2" color={'#9F9F9F'} size={'28'} onClick={handleInviteModal} style={{cursor: 'pointer'}} /> */}
                
                <Modal.Body className="py-5 px-4" >
                    <Row className="d-flex">
                        <Col lg={12} className="d-flex flex-column">
                            <p className="m-auto team-description">Copy the invitation link below and send it to your team member.</p>
                            <p className="m-auto team-description">Please note that this link is one-time only and is only valid for 24 hours.</p>
                        </Col>
                    </Row>

                    {/* <p className="m-auto team-token-link">Token of invitation:</p> */}
                    <Row className="d-flex my-3">
                        <Col md={1}/>
                        
                        <Col md={10} className="d-flex">
                            <Col md={11} className="team-token-container p-2 mx-0">
                                <p className="my-auto team-token-link">{`${window.location.origin}/${inviteToken.substring(0,33)}...`}</p>
                            </Col>
                            <Col md={1} className="d-flex p-0">
                                <CopyToClipboard 
                                    text={`${window.location.origin}/${inviteToken}`}
                                    onCopy={() => {
                                        Swal.fire({
                                            position: 'top-end',
                                            icon: 'success',
                                            title: 'Successfully copied to your clipboardd',
                                            showConfirmButton: false,
                                            timer: 1500
                                        })
                                    }}
                                >
                                <span className="d-flex team-copy-span mr-auto" style={{ cursor: 'pointer' }}><IoIosCopy className="m-auto" size={"22  "} color={"#fff"} /></span>
                                </CopyToClipboard>
                            </Col>
                        </Col>

                        <Col lg={1} />
                    </Row>

                    <Row className="justify-content-center my-4">
                        <Col md={6} className="d-flex">
                            <Image src={GRAY_SEPERATOR} fluid />
                        </Col>
                    </Row>
                    
                    <Row className="justify-content-center my-2">
                        <Col md={8} className="d-flex">
                            <p className="team-description mx-auto my-2">Or enter the email address of your team member</p>
                        </Col>
                    </Row>

                    <Row className="justify-content-center my-2">
                        <div md={4} className="d-flex">
                            <Form.Control type={"text"} className="ml-auto mr-3 my-auto team-token-link" value={nameTeamMember} disabled={true} />
                        </div>

                        <div md={4} className="d-flex">
                            <Form.Control type={"text"} className="ml-auto mr-3 my-auto team-token-link" value={emailTeamMember} onChange={(e) => setEmailTeamMember(e.target.value) } placeholder={"Email@member.com"} />
                        </div>

                        <div md={3} className="d-flex">
                            <div 
                                className={"team-button-send mr-3 text-center mr-auto my-auto"} 
                                onClick={() => {
                                    if(validateEmail(emailTeamMember)) {
                                        sendMailTeam(socialMediaPageSelected, emailTeamMember, nameTeamMember, inviteToken)
                                        .then(() => {
                                            setShowInviteModal(false);
                                        })
                                        .catch((err) => {
                                            console.log("Err modal send invitation Team", err)
                                            swalWithBootstrapButtons.fire({
                                                title: 'Error while sending invitation. Try again !',
                                                confirmButtonText: 'Retry',
                                            });
                                        })
                                    } else {
                                        swalWithBootstrapButtons.fire({
                                            title: 'Please enter a valid EMAIL',
                                            confirmButtonText: 'Retry',
                                        });
                                    }
                                }} 
                            >
                            Send invitation
                            </div>
                        </div>
                    </Row>
                </Modal.Body>
            </div>
        </Modal>
    );

    useEffect(() => {
        // resetSocialMediaSelections();
        resetَAllWizard();
    },[]);

    useEffect(() => {
        if(socialMediaPageSelected) {
            getTeamMembers(socialMediaPageSelected);
        };
    },[socialMediaPageSelected]);

    return (
        <div style={{height: window.innerHeight * 1.2 , marginTop: '135px'}}>

            {renderModal(socialMediaPageSelected)}

            <Row className="mt-5">
                <p className="home-big-title" >Team</p>
                <FaInfoCircle style={{transitionDuration: '600ms' }} color={!showPopover ? "#4080FF" : "#E5007D"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
                { showPopover && renderPopover() }
            </Row>

            <Pages title="Select a page to manage team" titleStyling={{color: '#4D4F5C', fontSize: '16px', fontFamily: 'Poppins Medium' }} />

            {socialMediaPageSelected
            &&
            <>
            {socialMediaPageSelected.status !== "listPageConnectedOtherTeam"
            ?
            <div>

            <Row className="mr-0 d-flex flex-column">                

                {socialMediaPageSelected.status === "listPageConnectedOwner"
                &&
                <Row className="mb-3"> 
                    <Col lg={12} className="d-flex">
                        <Form.Control type={"text"} className="mr-3 my-auto team-token-link" value={nameTeamMember} onChange={(e) => setNameTeamMember(e.target.value) } placeholder={"Member name"} />

                        <div 
                            className="team-invite-btn mr-auto d-flex"
                            style={{background: nameTeamMember.length >= 3 ? '#E5007D' : "#B4B4B4", border:  nameTeamMember.length >= 3 ? '#E5007D' : "#B4B4B4" }} 
                            onClick={() => {
                                if(nameTeamMember.length < 3) {
                                    swalWithBootstrapButtons.fire({
                                        title: 'Please enter a valid name first !',
                                        confirmButtonText: 'Retry',
                                    });
                                } else {
                                    setLoadingInvite(true);
                                    inviteMember(socialMediaPageSelected,nameTeamMember)
                                    .then((res) => {
                                        setInviteToken(res.data.data.tokenPage);
                                        getTeamMembers(socialMediaPageSelected);
                                        setTimeout(() => {
                                            setLoadingInvite(false);
                                            setShowInviteModal(true);
                                        },1500);
                                    })
                                    .catch(() => {
                                        setLoadingInvite(false);
                                        swalWithBootstrapButtons.fire({
                                            title: 'Error while generating the token',
                                            confirmButtonText: 'Retry',
                                        });
                                    });    
                                }
                            }}
                        >
                            {loadingInvite 
                            ?
                            (<>Generating token <Spinner animation="border" size="sm" variant="primary" className="spinnerWhite my-auto ml-2" /></>)
                            :
                            "Invite Member"
                            }
                        </div>
                    </Col>
                </Row>
                }

            <Row className="">
                <Col lg={5} className="d-flex">
                    <p className="ml-3 mr-auto my-auto team-members-titre" >Team members</p>
                </Col>
                <Col lg={5} className="d-flex">
                    <input type="text" placeholder="" className="ml-auto my-auto recherche-team py-2" />
                    <Zoom className={"zoom-icon"} color={""}  width="16" height="16" />
                </Col>
            </Row>

                <Col lg={10} className="p-3" style={{backgroundColor: socialMediaPageSelected.team.length ? 'white' : 'transparent', borderRadius: '0.3rem'}}>
                    {teamMembers && teamMembers.length
                    ?
                    <>

                    <Row>
                        <Col lg={4} className="d-flex pt-3 pb-3" >
                            <p className="ml-4 mr-auto my-auto manage-products-table-title">Name</p>
                        </Col>
                        
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="m-auto manage-products-table-title">Role</p>
                        </Col>

                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="mr-4 ml-auto my-auto manage-products-table-title">Actions</p>
                        </Col>
                        <hr  style={{borderColor: '#EBEDF0', opacity: '1',padding: '0',margin: 'auto',width: '94%'}} />
                    </Row>

                    {teamMembers.map((member) =>     
                    <Row key={member.idFacebook+Math.random()}>
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="ml-4 my-auto manage-product-entity-name">{member.name}</p>
                        </Col>
                        
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="m-auto manage-product-entity-name" style={{color : member.role.toUpperCase() === 'PENDING'  && '#E5007D' }}>{member.role.toUpperCase()}</p>
                        </Col>

                        <Col lg={4} className="d-flex pt-3 pb-3">
                            {
                            socialMediaPageSelected.status === "listPageConnectedOwner"
                            &&
                            <div className="ml-auto " style={{marginRight: '40px'}}>
                                {member.role === "owner"
                                ?
                                <TrashOwner className={""} color={""}  width="20" height="20" onClick={() => swalWithBootstrapButtons.fire({ title: "You can't delete the owner",confirmButtonText: 'Okay',}) }/>
                                :
                                <Trash 
                                    width="20" 
                                    height="20"
                                    style={{cursor: 'pointer'}} 
                                    onClick={() => { deleteTeamMember(socialMediaPageSelected, member.idFacebook, member.role)}} 
                                />
                                }
                            </div>
                            }
                        </Col>
                            <hr  style={{borderColor: '#EBEDF0', opacity: '1',padding: '0',margin: 'auto',width: '94%'}} />
                        {/* <Col lg={12}>
                        </Col> */}

                    </Row>
                    )}
                    </>
                    :
                    <>
                    <Col lg={12} className="d-flex" style={{backgroundColor: '#F9F9F9' }}>
                        <Lottie options={defaultOptionsNotFound} width={200} className="m-auto" /*height={400}*/ />
                    </Col>

                    <Col lg={12} className="d-flex mt-5" style={{backgroundColor: '#F9F9F9' }}>
                        <p className="m-auto home-big-title">Sorry ! You don't have any team member.</p>
                    </Col>
                    </>
                    }
                
                </Col>
                        
                <Col lg={2} />

                {/* <Col lg={10} className="mt-5 d-flex">
                    <nav className="m-auto" aria-label="Page navigation example">
                        <ul class="pagination">
                            <li class="page-item mx-2 d-flex p-0">
                            <a class="page-link d-flex py-0 px-2" href="#" aria-label="Previous">
                                <Left className={"m-auto"}  width="15" height="15" />
                            </a>
                            </li>
                            <li class="page-item mx-2"><a class="item-pagination-team page-link" href="#">1</a></li>
                            <li class="page-item mx-2"><a class="item-pagination-team page-link" href="#">2</a></li>
                            <li class="page-item mx-2"><a class="item-pagination-team page-link" href="#">3</a></li>
                            <li class="page-item mx-2 d-flex p-0">
                            <a class="page-link  d-flex py-0 px-2" href="#" aria-label="Next">
                                <Right className={"m-auto"} color={""}  width="15" height="15" />
                            </a>
                            </li>
                        </ul>
                    </nav>
                </Col>  */}


            </Row>
        </div>
            :
            <>
            <Col lg={12} className="d-flex" style={{backgroundColor: '#F9F9F9' }}>
                <Lottie options={defaultOptionsNotFound} width={200} className="m-auto" /*height={400}*/ />
            </Col>

            <Col lg={12} className="d-flex mt-5" style={{backgroundColor: '#F9F9F9' }}>
                <p className="m-auto home-big-title">Sorry ! You can't see this team</p>
            </Col>
            </>
            }
            </> 
            }
        
        </div>
    )
}

const mapStateToProps = (state) => ({
    socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    teamMembers : state.socialMediaR.teamMembers,
})

export default connect(mapStateToProps, { resetSocialMediaSelections,inviteMember, deleteTeamMember, sendMailTeam, getTeamMembers, resetَAllWizard, getAllPageConnected })(Team)
