import React, { useEffect, useState } from 'react'
import '../Home.css';

import PageCard from './PageCard';
import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import Lottie from 'react-lottie';

import { connect } from 'react-redux';
import { selectSocialMediaPage, getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import animationData from '../../../assets/json/loading.json';


function Pages(props) {

    const [loading, setLoading] = useState(true);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    useEffect(() => {
        // Getting insta & fb DATA :
        props.getInstaData();
        props.getFbData();
        setTimeout(() => {
            setLoading(false);
        },2000);
    },[]);

    useEffect(() => {
        console.log("Redux select =>", props.socialMediaPageSelected);
    },[props.socialMediaPageSelected]);


    // useEffect(() => {
    //     console.log("fb data", props.fbData);   
    // }[props.fbData,props.instaData]);

    // <img src={'https://graph.facebook.com/v3.2/' + props.page.idPage + '/picture?access_token=' + props.page.access_token} alt="page thumbnail" style={{ borderRadius: 50 + "%" }} />
      
    return (
        <>
        <Row>
            {
            props.title 
            ?
                <p className="home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
            :
                <p className="home-big-title">Pages Connected</p>
            }
        </Row>

        <Row>
            {
            !loading
            ?
                (
                    <>
                    <OwlCarousel margin={10} items={4}>
                       
                        {props.fbData !== null && 
                            Object.keys(props.fbData).map( (key, index) =>
                                props.fbData[key].map((page,index) => (
                                    <div onClick={ () => props.selectSocialMediaPage(page,"fb")}>
                                        <PageCard key={index} platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} />
                                    </div>
                                ))
                            )
                        }

                
                        {props.instaData !== null &&
                            Object.keys(props.instaData).map( (key, index) =>
                                props.instaData[key].map((page,index) => (
                                    <div onClick={ () => props.selectSocialMediaPage(page,"insta")}>
                                        <PageCard key={index} platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} />
                                    </div>
                                ))
                            )
                        }
                    </OwlCarousel>
                    </>
                )
            :
                (
                <div className="d-flex" style={{ width: '100%' }}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={200} /*height={400}*/ />
                    </div>
                </div>
                )
            }
            
        </Row>
        </>
    );
}

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { selectSocialMediaPage,getFbData, getInstaData })(Pages);
