import React, { useEffect, useState } from 'react';
import axios from 'axios';

import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';
import Lottie from 'react-lottie';
import animationData from '../../../assets/json/loadingSpinner.json';

import {
    useLocation
} from 'react-router-dom';

const DISC_ICON = require('../../../assets/images/home/disconnected-icon.png');
const CONN_ICON = require('../../../assets/images/home/connected-icon.png');
const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');
// const PROFILE_PHOTO = require('../../../assets/images/home/profile-photo.png');



export default function PageCard(props) {
    // ALL PROPS : platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} accessToken={page.access_token}

    const [fbGraphData,setFbGraphData] = useState(null);
    const [instaGraphData,setInstaGraphData] = useState(null);
    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };
    const fbHost = "https://graph.facebook.com/v8.0"

    useEffect(() => {

        if(props.platform === "fb") {
            axios.get(`${fbHost}/${props.idPage}/?fields=fan_count,picture,new_like_count&access_token=${props.accessToken}`)
                .then(res => { setFbGraphData(res.data); })
                .catch(err => { console.log("PageCardFb",(err)) })
            } else {
            // INSTA
            axios.get(`${fbHost}/${props.idPage}/?fields=id,media,followers_count,media_count,profile_picture_url,username,name&access_token=${props.accessToken}`)
                .then(res => { setInstaGraphData(res.data); })
                .catch(err => { console.log("PageCard Insta",(err)) })
        }
    }, [])

    useEffect(() => {
        const location = useLocation();
        
        console.log("loc", location);
    })

    return (
        /* if clicked border: 1px solid red; */
        <div className="pages-card m-1" style={{backgroundColor: "white", border: props.isConnectedPage ? "1px solid #E5007D" : "0"}}>
            <Row className="d-flex">
                <Image src={ props.isConnectedPage ? CONN_ICON : DISC_ICON} fluid className="ml-auto" />
            </Row>
                
            <Row className="d-flex">
                <Col className="d-flex flex-column">
                    {
                        props.picture !== null
                        ?
                        <Image src={props.picture} fluid className="m-auto pages-card-profile-picture" />
                        :
                        fbGraphData !== null 
                        ?
                        <Image src={fbGraphData.picture.data.url} fluid className="m-auto pages-card-profile-picture" />
                        :
                        <Lottie options={defaultOptions} width={150} /*height={400}*/ />
                    }
                        
                    <div className="d-flex" style={{zIndex: 1, marginTop: '-20px'}}>
                        <Col lg={6} />

                        <Col lg={6}>
                            <Image src={props.platform === "fb" ? FB_ICON : INSTA_ICON} fluid className="m-auto" />
                        </Col>
                    </div>
                </Col>
            </Row>

            <Row className="d-flex mb-3 mt-3">
                <p className="m-auto pages-card-title" >{props.namePage}</p>
            </Row>

            <Row className="d-flex mt-3">
                <Row className="m-auto justify-content-between">    
                    <Col lg={6}>
                        <Row>
                            <Col lg={12} className="d-flex">
                                {
                                instaGraphData !== null
                                ?
                                <p className="m-auto pages-card-title" style={{fontSize: '35px'}}>{instaGraphData.followers_count}</p>
                                :
                                fbGraphData !== null 
                                ?
                                    <p className="m-auto pages-card-title" style={{fontSize: '35px'}}>{fbGraphData.fan_count}</p>
                                :
                                    <Lottie options={defaultOptions} width={100} /*height={400}*/ />
                                }
                            </Col>

                            <Col lg={12} className="d-flex" >
                                <p className="m-auto pages-card-desc-2">Fans</p>
                            </Col>
                        </Row>
                    </Col>

                    <Col lg={6}>
                        <Row>
                            <Col lg={12} className="d-flex">
                                {
                                instaGraphData !== null
                                ?
                                    <p className="m-auto pages-card-title" style={{fontSize: '35px'}}>{instaGraphData.media_count}</p>
                                :
                                fbGraphData !== null
                                ?
                                    <p className="m-auto pages-card-title" style={{fontSize: '35px'}}>{fbGraphData.new_like_count}</p>
                                :
                                    <Lottie options={defaultOptions} width={100} /*height={400}*/ />
                                }
                            </Col>

                            <Col lg={12} className="d-flex">
                                {
                                props.platform === "fb"
                                ?
                                <p  className="m-auto pages-card-desc-2">Interactions</p>
                                :
                                <p  className="m-auto pages-card-desc-2">Posts</p>
                                }
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Row>
        </div>
    )
};
