const initialState = {
    fbData: null,
    instaData: null,
}

const socialMediaReducer = (state = initialState, action) => {

    
    switch (action.type) {
        case 'GET_FB_DATA' :
            return {
                ...state,
                fbData : action.payload,
            }
        case 'GET_INSTA_DATA' :
            return {
                ...state,
                instaData : action.payload,
            }

        default:
            return state;
    }
}

export default socialMediaReducer;
