import React, { useEffect } from 'react'
import { connect } from 'react-redux';
import { nextWizardStep, testAgent } from '../../../redux/actions/wizardActions';

import {
    Row,
    Col,
    Image,
    // Form,
    // Popover,
} from 'react-bootstrap';

import {
    Link
} from 'react-router-dom';

const IMODO_PROFILE_ICON = require('../../../assets/images/home/full-bulle-imodo.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const BALISE_ICON = require('../../../assets/images/home/balise.png');

// import { FaTrashAlt } from 'react-icons/fa';
// import { IoIosArrowDown } from 'react-icons/io';
// import { AiOutlineCloseCircle } from 'react-icons/ai';
// import { MdAddCircleOutline } from 'react-icons/md';

export const WizardTest = (props) => {

    useEffect(() => {
        props.testAgent();
    },[]);

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col className="wizard-pages-container mb-3" style={{backgroundColor: 'white'}}>
                
                <Row className="d-flex mt-4 ml-2 mb-4">
                    <p className="my-auto wizard-pages-title">Test Interaction</p>
                </Row>


                
                <Row className="mb-4">
                    <Col lg={9} className="mx-auto" style={{backgroundColor : 'white',borderRadius: '0.2rem',border: '1px solid #B4B4B4'}}>
                       
                        {/* BULLE GRIS */}
                        <Row className="d-flex">
                            <Col>
                                <Row className="my-3">
                                    <Col>
                                        <div className="my-agents-msgs-bulle mb-1">
                                            <p className="my-agents-msgs-bulle-text">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna</p>
                                        </div>

                                        {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                    </Col>
                                </Row>
                            </Col>

                            <Col lg={7} />
                        </Row>
                        
                        {/* BULLE BLEU */}
                        <Row className="d-flex">
                            <Col lg={6} />

                            <Col>
                                <Row className="my-3">
                                    <Col className="d-flex">
                                        <div className="my-agents-msgs-bulle-bleu mb-1" style={{backgroundColor: '#3B86FF'}}>
                                            <p className="my-agents-msgs-bulle-text" style={{color: 'white'}}>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna</p>
                                        </div>
                                        <Col lg={2} className="d-flex mr-2">
                                            <Image src={IMODO_PROFILE_ICON} className={"mt-auto"} style={{maxWidth: '50px'}} />
                                        </Col>
                                        {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                    </Col>
                                </Row>
                            </Col>

                        </Row>
                
                    </Col>
                </Row>


                <Row className="mt-3 mb-3">
                    <Col lg={9} className="mx-auto" style={{backgroundColor : 'white',borderRadius: '0.2rem',border: '1px solid #B4B4B4'}}>
                        <Row className="pt-4 pb-4">

                            <Col lg={2}>
                                <Row className="justify-content-around">
                                    <div className="d-flex">
                                        <Image src={BALISE_ICON} className="m-auto my-agents-msgs-tiny-icon" />    
                                    </div>                                

                                    <div className="d-flex">
                                        <Image src={EMO_ICON} className="m-auto my-agents-msgs-tiny-icon" />
                                    </div>

                                    <div className="my-agents-msgs-tiny-box d-flex">
                                        <Image src={LETTER_ICON} className="m-auto my-agents-msgs-tiny-icon" />
                                    </div>
                                </Row>
                            </Col>

                            <Col lg={6} className="d-flex p-0">
                                <input type="text"  className="my-auto" placeholder="Your Message..." />
                            </Col>

                            <Col lg={3} />    

                        </Row>
                    </Col>
                </Row>


                <Row className="d-flex justify-content-between pl-4 pr-4">
                    <div className="wizard-pages-inactive-btn mb-3">
                        {/* MUST RESET ALL WIZARD VARS */}
                        <Link to="/home/pages" style={{textDecoration: 'none', color: '#818E94'}} onClick={() => props.resetWizardStep()} >
                            Back
                        </Link>
                    </div>

                    <div className="mb-3">
                        <Row>
                            <div className="wizard-pages-inactive-btn mr-3">
                                Save Draft
                            </div>

                            <div className="wizard-pages-active-btn">
                                Finish
                            </div>
                        </Row>
                    </div>
                </Row>

            </Col>
    
            <Col lg={1} />
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
})

export default connect(mapStateToProps, { nextWizardStep, testAgent })(WizardTest)
