import React, { useEffect , useState} from 'react';
import '../Home.css';

import Pages from '../Pages/Pages';

import moment from 'moment';

import { connect } from 'react-redux';                       
import { resetSocialMediaSelections, } from '../../../redux/actions/socialMediaActions';

import {
    Row,
    Col,
    Image,
    Dropdown,
} from 'react-bootstrap';

// Translations
import { trans } from '../../../Translations';

import { FaInfoCircle } from 'react-icons/fa';

function Billing(props) {
    
    let {
        lang,
        socialMediaPageSelected,
        
    } = props;

    const [showPopover, setShowPopover] = useState(false);
    const [choice, setChoice] = useState({ id:"plan", value: trans[lang].billing.billingPlan});


    const renderPopover = () => (
        <div className="d-flex manage-products-popover" onMouseEnter={() => setShowPopover(true)} onMouseLeave={() => setShowPopover(false)}>
          <p className="m-auto">{trans[lang].billing.infoPopover}</p>
        </div>
    );
      
    const handlePopover = (delay) => {
        setTimeout( () => {
          setShowPopover(!showPopover);
        },delay)
    };

    
    return (
        <div style={{ marginTop: '135px' }}>
             <Row className="mt-5">
                <p className="home-big-title">{trans[lang].billing.title}</p>
                <FaInfoCircle style={{transitionDuration: '300ms' }} color={!showPopover ? "#4080FF" : "#E5007D"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('700')} />
                {showPopover && renderPopover()}
            </Row>

            {/* Top bar */}
            <Row>
                <Col lg={12} className="d-flex p-0">
                    <div className={choice.id === "plan" ? "billing-choice-box-active d-flex mr-3" : "billing-choice-box d-flex mr-3"} onClick={() => setChoice({ id:"plan", value: trans[lang].billing.billingPlan})}>
                        <p className="m-auto">{trans[lang].billing.billingPlan}</p>
                    </div>
                    
                    <div className={choice.id === "change" ? "billing-choice-box-active d-flex mr-3" : "billing-choice-box d-flex mr-3"} onClick={() => setChoice({ id:"change", value: trans[lang].billing.changePlan})}>
                        <p className="m-auto">{trans[lang].billing.changePlan}</p>
                    </div>
                    
                    <div className={choice.id === "payments" ? "billing-choice-box-active d-flex mr-3" : "billing-choice-box d-flex"} onClick={() => setChoice({ id:"payments", value: trans[lang].billing.payments})}>
                        <p className="m-auto">{trans[lang].billing.payments}</p>
                    </div>
                </Col>

                {
                choice.id === "plan"
                ?
                <Col lg={12} className="d-flex p-0 mt-5">
                    <div className="mr-auto billing-box px-3 py-2">

                        <div className="d-flex mb-3">
                            <p className="mr-auto my-auto billing-plan-gray-text">{trans[lang].billingPlan.subscribedTo} Basic Plan</p>
                            <div className="ml-auto my-auto d-flex billing-plan-pink-btn py-2 px-4">
                                <p className="m-auto">Basic</p>
                            </div>
                        </div>

                        <div>
                            <Col lg={5} className="p-0">
                                <Col lg={12} className="d-flex mb-2 p-0">
                                    <p className="mr-auto">{trans[lang].billingPlan.socialPages}</p>
                                    <p className="ml-auto">2/2</p>
                                </Col>
                            
                            </Col>
                        </div>


                    </div>
                </Col>
                :
                choice.id === "change"
                ?
                <Col lg={12} className="d-flex p-0">
                    <p>Change</p>
                </Col>
                :
                <Col lg={12} className="d-flex p-0">
                    <p>Payments</p>
                </Col>
                }
            </Row>
        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        lang : state.socialMediaR.lang,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { resetSocialMediaSelections })(Billing);
