import io from 'socket.io-client';
import { host } from '../config'
import { connect } from 'react-redux';
// import { connect } from 'react-redux';
import { chatAction } from '../actions/index'
let socket = io.connect(host, { secure: true });

export function sendMessage(obj) {
    socket.emit("chat.message", obj)
}

export function getMessage(dispatch) {
    socket.on("chat.message", (message) => {
        console.log('test message', message)
        chat(message)
    })
}
const mapDispatchToProps = (dispatch) => ({
    chat: (message) => dispatch(chatAction(message))
})
export default connect(null, mapDispatchToProps)(socket)
