import { useMediaQuery } from 'react-responsive';

export const host = 'https://node3.mobilesupportbot.com:5075';

export const trainAgentHost = 'https://node2.mobilesupportbot.com:5051';

// For responsive design
export const isDesktop = useMediaQuery({ minWidth: 992 }); 

export const isTablet = useMediaQuery({ minWidth: 768, maxWidth: 991 }); 

export const isMobile = useMediaQuery({ maxWidth: 767 });
