const initialState = []

const entitys = (state = initialState, action) => {
    switch (action.type) {
        case 'GETALLENTITYS':
            return action.entitys.data.data.flat()
        case 'ADDENTITY':
            const newentity = {
                name: action.entitys.name,
                children: [
                    {
                        content: action.entitys.children[0].content,
                        synonyms: action.entitys.children[0].synonyms
                    }
                ]
            }
            return [...state, newentity]
        default:
            return state
    }
}
export default entitys
