const initialState = []

const entitys = (state = initialState, action) => {
    switch (action.type) {
        case 'GETALLENTITYS':
            return action.entitys.data.data.flat()
        case 'ADDENTITY':
            return [...state, action.entitys]
        default:
            return state
    }
}
export default entitys
