        </div>
    );

    return (
        <div style={{height: window.innerHeight * 1.2}}>
           
           <Row className="mt-5">
                <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Add category</p>
                <FaInfoCircle color={"#4080FF"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
                { showPopover && renderPopover() }
            </Row>

            {/* SELECTED PAGE */}
            { 
            props.socialMediaPageSelected !== null &&
                <Row className="mr-auto d-flex mb-3">
                    <Row className="wizard-selected-page-container m-auto p-3">
                        <div className="d-flex flex-column mr-3">
                            <Image src={props.socialMediaPageSelected.picture_url} className="m-auto" style={{maxWidth: '40px'}} />
                            <Image src={props.socialMediaPageSelected.platform === "insta" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                        </div>
                        <p className="m-auto wizard-pages-page-title">{props.wizardSelectedPage.name}</p> 
                    </Row>
                </Row>
            }



        </div>
    );
}

const mapStateToProps = (state) => {
    return {
      socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};


export default connect(mapStateToProps, {})(AddNewProducts)
