import React, { useEffect, useState } from 'react'

import Pages from '../Pages/Pages';

import {
    Col,
    Row,
    Image,
    Modal,
} from 'react-bootstrap';
import ReactTooltip from 'react-tooltip';
import moment from 'moment';
import { useHistory, useLocation  } from 'react-router-dom';

import { connect } from 'react-redux';
import {  resetSocialMediaSelections, } from '../../../redux/actions/socialMediaActions';
import { resetَAllWizard, } from '../../../redux/actions/wizardActions';
import {  getProjectByPage, goToWizardConfig, deleteProjectByPage, setAgentsSelectedProject, resetLogs  } from '../../../redux/actions/myAgentsActions';

import { FaLink } from 'react-icons/fa';
import { AiFillPicture } from 'react-icons/ai';
import { BiError } from 'react-icons/bi';
import { IoIosClose } from 'react-icons/io';
// const PROJECT_PHOTO = require('../../../assets/images/home/project-photo.png');
const EDIT_ICON = require('../../../assets/images/home/edit-icon.png');
const MSG_ICON = require('../../../assets/images/home/message-icon.png');
const TRASH_ICON = require('../../../assets/images/home/trash-icon.png');


function MyAgents(props) {

    let {
        socialMediaPageSelected,
        agentsProjects,
        
        // Functions
        resetَAllWizard,
        resetSocialMediaSelections,
        resetLogs,
        getProjectByPage,
        goToWizardConfig,
        deleteProjectByPage,
        setAgentsSelectedProject
    } = props;

    let history = useHistory();
    let location = useLocation();

    const tableTitles = [
        {title : 'Picture'},
        {title : 'Publish'},
        {title : 'Message'},
        {title : 'Date'},
        {title : 'Type'},
        {title : 'Status'},
        {title : 'Edit project'},
    ];

    // State :
    const [deleteProjectModal, setDeleteProjectModal] = useState(false);
    const [projectToDelete, setProjectToDelete] = useState({});

    const confirmDeleteModal = () => (
        <Modal show={deleteProjectModal} centered>
          <Modal.Body>
            <Col lg={12}>
              <Row>
                <Col lg={12} className="d-flex">
                  <IoIosClose color={"#B4B4B4"} size={30} className="ml-auto" style={{cursor: 'pointer'}} onClick={() => { setProjectToDelete({}); setDeleteProjectModal(false); }} />
                </Col>
  
                <Col lg={12} className="d-flex">
                  <BiError color={"#E5007D"} size={65} className="mx-auto mb-3" style={{cursor: 'pointer'}} />
                </Col>
  
                <Col lg={12} className="d-flex">
                  <p className="mx-auto mb-2 confirm-modal-text">
                  Are you sure you want to delete this project?
                  </p>
                </Col>
                
                <Col lg={12} className="d-flex">
                  <p className="mx-auto mb-3 confirm-modal-text">
                  This will remove all products used in this project
                  </p>
                </Col>
  
                <Col lg={12} className="d-flex">
                  <div className="mx-auto mb-3 manage-products-add-btn d-flex text-center" onClick={() => deleteProjectByPage(projectToDelete).then(() => { setProjectToDelete({}); setDeleteProjectModal(false); })}>
                    <p className="m-auto">Yes, delete it!</p>
                  </div>
                </Col>
  
                <Col lg={12} className="d-flex">
                  <p className="mx-auto mb-2 cancel-btn-modal-text" onClick={() => { setProjectToDelete({}); setDeleteProjectModal(false); }}>Cancel</p>
                </Col>
              </Row>
            </Col>
          </Modal.Body>
        </Modal>
    );

    const renderDate = (date) => {
        return moment(date).format('DD/MM/YY');
    };

    const renderTime = (date) => {
        return moment(date).format('h:mm');
    };

    const checkStatusClass = (trained,live) => {
    
        if (trained && live) {
            // ACTIVE
            return "my-agent-status-btn d-flex"

        } else if (trained && !live) {
            // !NOT ACTIVE
             return "my-agent-status-btn d-flex"
        } else {
            // DRAFT
            return "my-agent-status-btn-disabled d-flex"
        }
    };

    useEffect(() => {
        resetَAllWizard();
        resetLogs();
        if(location.state) {
            if(location.state.hasOwnProperty('imFrom')) {
                // Don't Reset
            } else {
                resetSocialMediaSelections();
            }
        }
    },[]);

    useEffect(() => {
        if(socialMediaPageSelected) {
            getProjectByPage(socialMediaPageSelected);
        }
    },[socialMediaPageSelected])

    return (
    <div style={{ height: window.innerHeight * 2, marginTop: '135px' }}>
        {/* MODAL DELETE PROJECT */}
        {deleteProjectModal && confirmDeleteModal()}
        <Row>
          <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Agents management</p>
        </Row>

        <Pages title="Select a page to manage agents" titleStyling={{color: '#4D4F5C', fontSize: '16px', fontFamily: 'Poppins Medium' }} />

        {props.socialMediaPageSelected &&
        <>
        <Row>
            <Col lg={12}>
                <Row className="mb-2 mx-0">

                    <Col lg={12} className="d-flex mb-0">
                        <p className="my-auto my-agent-table-title">Projects List</p>
                    </Col>
            
                    <Col lg={11} className="my-3 mx-0">
                        <Row>
                            <Col lg={12} className="p-4" style={{background: "#FFFFFF"}}>
                                <Row className="py-2 justify-content-between">
                                    {tableTitles.map((item,index) => (
                                        <Col lg={item.title === "Edit project" ? 2 : 1} key={index} className="d-flex">
                                            <div className="my-auto wizard-pages-page-title-table" >{item.title}</div>
                                        </Col>    
                                    ))}   
                                </Row>  
                                
                                {/* PROJECTS */}
                                {agentsProjects && 
                                agentsProjects.map((project,indexProject) => 
                                    <Row key={indexProject} className="py-2 justify-content-between" >  
                                        <Col lg={1} className="d-flex">
                                                {project.post.picture 
                                            ?
                                            <Image src={project.post.picture} className={"my-auto"} style={{height: '40px', width: '40px'}} />
                                            :
                                            <Image src={socialMediaPageSelected.picture_url} className={"my-auto"} style={{height: '40px', width: '40px', borderRadius: '50%'}} />
                                            }
                                        </Col>    

                                        <Col lg={1} className="d-flex">
                                            <div className="flex-column">
                                                {project.post.published
                                                &&
                                                <>
                                                <p className="m-auto my-agent-project-date"  style={{color: project.post.status === "Scheduled" ? "#009EE3" : "#4D4F5C"}}>{renderDate(project.post.published)}</p>
                                                <p  className="m-auto my-agent-project-time" style={{color: project.post.status === "Scheduled" ? "#009EE3" : "#4D4F5C"}}>{renderTime(project.post.published)}</p>
                                                </>
                                                }
                                            </div>
                                        </Col>    

                                        <Col lg={1} className="d-flex" >
                                            {project.post.message &&
                                            <div className="m-auto my-agents-msg-desc" >
                                                {project.post.message.length > 12
                                                ?
                                                project.post.message.substring(0,9) + "..."
                                                :
                                                project.post.message
                                                }
                                            </div>
                                            }
                                        </Col>    

                                        <Col lg={1} className="d-flex" >
                                            <div className="flex-column">
                                                <p className="m-auto my-agent-project-date">{renderDate(project.createDate)}</p>
                                                <p  className="m-auto my-agent-project-time">{renderTime(project.createDate)}</p>
                                            </div>
                                        </Col>

                                        <Col lg={1} className="d-flex" >
                                            <div className="m-auto wizard-pages-page-title-table" >
                                                {project.post.picture
                                                ?
                                                <AiFillPicture color={"#B4B4B4"} size={"22"} className="m-auto" />
                                                :
                                                <FaLink color={"#B4B4B4"} size={"22"} className="m-auto" />
                                                }
                                            </div>
                                        </Col>    

                                        <Col lg={1}>
                                            <div className={checkStatusClass(project.trained,project.live)}>
                                                {/* "my-agent-status-btn text-center" */}
                                                {
                                                project.trained && project.live 
                                                ? 
                                                <p className="m-auto">Active</p> 
                                                : 
                                                project.trained && !project.live
                                                ? 
                                                <p className="m-auto">Not Active</p>
                                                :
                                                 <p className="m-auto">Draft</p>
                                                }
                                            </div>
                                        </Col>   

                                        <Col lg={2} className="d-flex" >
                                            <Image src={EDIT_ICON}  data-for='editIcon' data-tip={"Update agent"} className={"my-auto my-agents-icon"}                 style={{ height: '22.02px', width: '22.02px'}} onClick={() => goToWizardConfig(project,socialMediaPageSelected,history)} />
                                            <Image src={MSG_ICON}   data-for='commentsIcon' data-tip={"Check comments"} className={"ml-2 mr-2 my-auto my-agents-icon"} style={{ height: '25px', width: '25px'}}       onClick={() => setAgentsSelectedProject(project,history) } />
                                            <Image src={TRASH_ICON} data-for='deleteIcon' data-tip={"Delete agent"} className={"my-auto my-agents-icon"}               style={{ height: '29px', width: '27px'}}       /*onClick={() => deleteProjectByPage(project)}*/ onClick={() => {setProjectToDelete(project); setDeleteProjectModal(true); }} />
                                            
                                            <ReactTooltip id='editIcon'     className='my-agent-icons-hover-text' textColor='#fff' backgroundColor='#B4B4B4' />
                                            <ReactTooltip id='commentsIcon' className='my-agent-icons-hover-text' textColor='#fff' backgroundColor='#B4B4B4' />
                                            <ReactTooltip id='deleteIcon'   className='my-agent-icons-hover-text' textColor='#fff' backgroundColor='#B4B4B4' />
                                        </Col>    
                                    </Row>  
                                )}
                            </Col>
                        </Row>
                    </Col>

                </Row>
            </Col>
        </Row>
        </>
        }

    </div>
    );
};

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        agentsProjects: state.agentsR.agentsProjects,
        agentsSelectedProject: state.agentsR.agentsSelectedProject,
    }
};

export default connect(mapStateToProps, { resetSocialMediaSelections, getProjectByPage, goToWizardConfig, resetَAllWizard, deleteProjectByPage, setAgentsSelectedProject, resetLogs  })(MyAgents);
