import React, { useEffect } from 'react'
import { connect } from 'react-redux';
import { nextWizardStep, getAllIntents, addProject } from '../../../redux/actions/wizardActions';

import {
    Row,
    Col,
    // Popover,
    // Form,
    // Image,
} from 'react-bootstrap';

import {
    Link
} from 'react-router-dom';

import { FaTrashAlt } from 'react-icons/fa';
import { IoIosArrowDown } from 'react-icons/io';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import { MdAddCircleOutline } from 'react-icons/md';

export const WizardConfig = (props) => {

    const intents = [
        'Price',
        'Location',
        'Time',
        'Event',
        'Greeting',
    ];

    useEffect(() => {
        props.getAllIntents(props.wizardIntentType);
    },[]);

    useEffect(() => {
        console.log("WizardConfig intents :", props.wizardIntents);
    }, [props.wizardIntents])

    // const popover = (
    //     <Popover id="popover-basic">
    //       <div>
    //           FREE POPOVER :3
    //       </div>
    //     </Popover>
    //   );

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col className="wizard-pages-container mb-3" style={{backgroundColor: 'white'}}>
                
                {/* INTENTS */}
                <Row className="d-flex">
                    <Col lg={6} className="d-flex flex-column p-3">
                        
                        <div className="d-flex flex-column wizard-config-intents-container">
                            <p className="mr-auto ml-3 p-2 wizard-config-intent-title">Select Intents</p>
                            
                            <Row className="ml-1 mr-1 mb-3">
                                {intents.map((item, index) => (
                                    <Col lg={3} className="d-flex ml-4 mb-3 pt-2 pb-2 wizard-config-intent-box">
                                        <p className="m-auto wizard-config-intent-name">{item}</p>
                                    </Col>
                                ))}
                            </Row>
                        </div>
                        
                    </Col>

                    <Col />
                </Row>

                {/* INTENTS DETAILS */}
                <Row className="d-flex mt-5">    
                    
                    <Col lg={5} className="mb-3 mr-3">
                        
                        <div className="d-flex flex-column wizard-config-intents-container" style={{borderRadius: '0.5rem'}}>
                            
                            <div className="justify-content-between d-flex flex-row mx-0 p-0  wizard-config-intents-top-row">
                                <p className="my-auto wizard-config-intent-title p-2">Price</p>

                                <div className="my-auto d-flex p-2">
                                    <FaTrashAlt  color={"#818E94"} size={"20"} className="m-auto" style={{cursor: 'pointer'}} />
                                    <IoIosArrowDown color={"#818E94"} size={"20"} className="my-auto ml-2" style={{cursor: 'pointer'}} />
                                </div>
                            </div>
                            
                            <div className="mr-2 ml-2 mt-2 mb-2">
                                <label className="wizard-config-textarea-label">Generic Response</label>
                                <textarea className="wizard-config-textarea" rows="5"/>
                            </div>

                            <div className="d-flex">
                                <div className="ml-auto mr-2 wizard-config-update-btn">
                                    UPDATE
                                </div>
                            </div>

                            {/* PRODUCTS */}
                            <Col lg={11} className="mx-auto mt-4">
                                <Row className="justify-content-between">    
                                    {intents.map((item, index) => (
                                        <Col lg={5} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-product-box">
                                            <p className="m-auto wizard-config-product-name">{item}</p>
                                            <AiOutlineCloseCircle className="my-auto ml-1" color={"#F49BCC"} size={"20"} style={{cursor: 'pointer'}} />
                                        </Col>
                                    ))}
                                    <Col lg={5} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-add-product-box">
                                        <MdAddCircleOutline className="my-auto mr-2" color={"#4080FC"} size={"20"} style={{cursor: 'pointer'}} />
                                        <p className="my-auto wizard-config-add-product-name">Add product</p>
                                    </Col>
                                </Row>
                            </Col>

                        </div>
                    </Col>
                   
                </Row>
                
                <Row className="d-flex justify-content-between pl-4 pr-4">
                    <div className="wizard-pages-inactive-btn mb-3">
                        {/* MUST RESET ALL WIZARD VARS */}
                        <Link to="/home/pages" style={{textDecoration: 'none', color: '#818E94'}} onClick={() => props.resetWizardStep()} >
                            Back
                        </Link>
                    </div>

                    <div className="mb-3">
                        <Row>
                            <div className="wizard-pages-inactive-btn mr-3">
                                Save Draft
                            </div>

                            <div className="wizard-pages-active-btn" onClick={() => props.nextWizardStep()}>
                                Next
                            </div>
                        </Row>
                    </div>
                </Row>

            </Col>
    
            <Col lg={1} />
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardIntents: state.wizardR.wizardIntents,
})

export default connect(mapStateToProps, {nextWizardStep, getAllIntents, addProject})(WizardConfig)
