const mapStateToProps = state => ({
  wizardIdProject: state.wizardR.wizardIdProject,
  wizardIntentType: state.wizardR.wizardIntentType,
  wizardSelectedPage: state.wizardR.wizardSelectedPage,
  wizardSelectedPost: state.wizardR.wizardSelectedPost // wizardStep: state.wizardR.wizardStep,

});

export default connect(mapStateToProps, {
  addGenericResponse,
  updateGenericResponse
})(WizardIntentDetails);
