import React, { useState, useEffect } from 'react'
import './Login.css';
import Facebook from './Facebook';

// Components :
import Navbar from '../Components/Navbar';

import {
    Container,
    Row,
    Col,
    Image,
    // Nav,
} from 'react-bootstrap';

import {
    Link,
    Redirect,
} from 'react-router-dom';

// Icons & Images:
const LANDING_IMG = require('../../assets/images/login/landing-img.png');
const MIND_ICON = require('../../assets/images/login/mind.png');
const CHATBOT_ICON = require('../../assets/images/login/chatbot.png');
const CORE_AI_ICON = require('../../assets/images/login/coreai.png');
const MIND_ICON_W = require('../../assets/images/login/mind_w.png');
const CHATBOT_ICON_W = require('../../assets/images/login/chatbot_w.png');
const CORE_AI_ICON_W = require('../../assets/images/login/coreai_w.png');


function LandingPage() {

    const [hoverCard,setHoverCard] = useState(false);
    const [hoverCard1,setHoverCard1] = useState(false);
    const [hoverCard2,setHoverCard2] = useState(false);

    const auth_token = localStorage.getItem('authtoken');
    const userData = JSON.parse(localStorage.getItem('userData'));

    const allMenuItems = [
        { title: 'What is Imodo' , route: 'link-what-is' },
        { title: 'How to use it' , route: 'link-how-to-use' },
        { title: 'For developpers' , route: 'link-for-devs' }
    ];

    return (
        <>
        { auth_token !== null && userData !== null && <Redirect to="/home/pages" /> }
        <Navbar menuItems={allMenuItems} loginBtn={true} />

        <Container fluid>
            {/* BLOC 1 */}
            <Row className="mb-5 justify-content-center" style={{background: "#F9F9F9" , padding: '63px 0px'}}>
                {/* <Col lg={2} className={"mt-5"} /> */}

                {/* <Col lg={4} className={"d-flex flex-column justify-content-around p-5 landing-texts-container"}> */}
                <Col lg={4} className={"d-flex"} style={{backgroundColor: ''}} >
                    {/* <div className="m-auto d-flex justify-content-between flex-column"> */}
                    <div className={"mx-auto mt-auto mb-5"}>
                        <Row className={""}>
                            <Col lg={12} className="d-flex"> <p className="landing-big-text ">Conversation is <br/> a first step of a business </p></Col>
                            <Col lg={12} className="d-flex"><p className="landing-desc mb-5 mt-4">Streamline your Moderation <br/> team efforts</p></Col>
                            <Col lg={12} className="d-flex mt-3"> <Facebook type="landing" content="START MY FREE TRIAL"/> </Col>
                        </Row>
                    </div>
                    {/* </div> */}
                </Col>

                <Col lg={6} className={"d-flex"} style={{backgroundColor: ''}}>
                {/* <Col lg={4} className={"m-auto p-5"} style={{backgroundColor: 'gold'}}> */}
                    <Image  className={"mx-auto mt-2"} src={LANDING_IMG} style={{ maxWidth: '650px'}}/>
                </Col>
                
                {/* <Col lg={2} /> */}
            </Row>


            {/* CARDS */}
            {/* <Row className="d-flex justify-content-around m-5"  style={{paddingTop: '100px'}}> */}
            <Row className="justify-content-center"  style={{paddingTop: '100px',paddingBottom: '100px'}}>
                <Col xs={10} md={10} lg={10} xl={10} className="">
                    <Row className="justify-content-center" >

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="my-auto mr-auto landing-card" onMouseEnter={() => setHoverCard(true) } onMouseLeave={() => setHoverCard(false) }>
                                <Row className="d-flex mb-3">
                                    {/* <Image src={MIND_ICON} fluid className="mr-auto landing-card-icon" style={{maxWidth: '64px'}} /> */}
                                    <Image src={hoverCard ? MIND_ICON_W : MIND_ICON } fluid className="mr-auto landing-card-icon" />
                                    {/* <MindLogo className="mr-auto landing-card-icon" fill={hoverCard && "white"} stroke={hoverCard && "white"} /> */}
                                </Row>
                                <Row>
                                    <p className="landing-card-title">Own machine learning</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo </p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="m-auto landing-card" onMouseEnter={() => setHoverCard1(true) } onMouseLeave={() => setHoverCard1(false) }>
                                <Row className="d-flex mb-3">
                                    <Image src={hoverCard1 ? CHATBOT_ICON_W : CHATBOT_ICON } fluid className="mr-auto landing-card-icon"/>
                                    {/* <ChatBot className="mr-auto landing-card-icon" fill={hoverCard1 && "white"} stroke={hoverCard1 && "white"} /> */}

                                </Row>
                                <Row>
                                    <p className="landing-card-title">Chatbot solution</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo </p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="my-auto ml-auto landing-card" onMouseEnter={() => setHoverCard2(true) } onMouseLeave={() => setHoverCard2(false) }>
                                <Row className="d-flex mb-3">
                                    <Image src={hoverCard2 ? CORE_AI_ICON_W : CORE_AI_ICON } fluid className="mr-auto landing-card-icon"/>
                                    {/* <Coreai className="mr-auto landing-card-icon" fill={hoverCard2 && "white"} stroke={hoverCard2 && "white"} /> */}

                                </Row>
                                <Row>
                                    <p className="landing-card-title">Exceptional Core AI</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo </p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>
                        
                    </Row>
                </Col>
            </Row>
        </Container>
        </>
    )
}

export default LandingPage;
