import React, { useState, useRef } from 'react';

import {
    Row,
    Col,
    Popover,
    Overlay,
    Button,
} from 'react-bootstrap';


import { FaTrashAlt } from 'react-icons/fa';
import { IoIosArrowDown } from 'react-icons/io';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import { MdAddCircleOutline } from 'react-icons/md';


export default function WizardIntentDetails(props) {

    const [intentState, setIntentState] = useState('save');
    // const [intentClosed, setIntentClosed] = useState(false);

    const [show, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);

  const handleClick = (event) => {
    setShow(!show);
    setTarget(event.target);
  };

    return (
                  
        <Col lg={5} className="mb-3 mr-3">
            
            <div className="d-flex flex-column wizard-config-intents-container" style={{borderRadius: '0.5rem'}}>
                
                <div className="justify-content-between d-flex flex-row mx-0 p-0  wizard-config-intents-top-row">
                    <p className="my-auto wizard-config-intent-title p-2">Price</p>

                    <div className="my-auto d-flex p-2">
                        <FaTrashAlt  color={"#818E94"} size={"20"} className="m-auto" style={{cursor: 'pointer'}} />
                        <IoIosArrowDown color={"#818E94"} size={"20"} className="my-auto ml-2" style={{cursor: 'pointer'}} />
                    </div>
                </div>
                
              
                <div>
                    <div className="mr-2 ml-2 mt-2 mb-2">
                        <label className="wizard-config-textarea-label">Generic Response</label>
                        <textarea className="wizard-config-textarea" rows="5"/>
                    </div>

                    <div className="d-flex">
                        {
                            intentState === 'save' 
                            ?
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn" onClick={() => setIntentState('update')}>Save</div>)
                            :
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn">Update</div>)
                        }
                    </div>

                    {/* PRODUCTS */}
                    {
                        intentState === 'update' &&
                        <Col lg={11} className="mx-auto mt-4">
                            <Row className="justify-content-between">    
                                <Col lg={5} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-product-box">
                                    <p className="m-auto wizard-config-product-name">hlalo</p>
                                    <AiOutlineCloseCircle className="my-auto ml-1" color={"#F49BCC"} size={"20"} style={{cursor: 'pointer'}} />
                                </Col>
                            
                                <Col lg={5} onClick={handleClick} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-add-product-box">
                                    <MdAddCircleOutline className="my-auto mr-2" color={"#4080FC"} size={"20"} style={{cursor: 'pointer'}} />
                                    <p className="my-auto wizard-config-add-product-name">Add product</p>
                                </Col>

                                <div ref={ref}>
                                    <Overlay
                                        show={show}
                                        target={target}
                                        placement="right"
                                        container={ref.current}
                                        containerPadding={50}
                                    >
                                        <Popover id="popover-contained">
                                        <Popover.Title as="h3">Popover bottom</Popover.Title>
                                        <Popover.Content>
                                            <strong>Holy guacamole!</strong> Check this info.
                                        </Popover.Content>
                                        </Popover>
                                    </Overlay>
                                </div>

                            </Row>
                        </Col>
                    }
                </div>
            
            </div>
        </Col>
   
    )
}
