import axios from 'axios'
import { host } from '../../config';

export const getAllProductCategories = (wizardSelectedPage) => {
    return (dispatch) => {
    
        // console.log("categories with => ", wizardSelectedPage.platform, wizardSelectedPage.id);
        return axios.get(host + `/api/v1/secure/project/get/entitiesproject/${wizardSelectedPage.platform}/${wizardSelectedPage.id}`, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then(res => {
            console.log('Social media actions => all Categories for this Page = ', res.data.data)
            
            // dispatch({
            //     type: 'GET_ALL_PRODUCT_CATEGORIES',
            //     payload: res.data.data
            // })
        })
        .catch((err) => console.log("err getConnectedPagesProject", err))
    }
};

export const getConnectedPagesProject = () => {
