import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux';
import { nextWizardStep, getAllIntents, } from '../../../redux/actions/wizardActions';
import Swal from 'sweetalert2/dist/sweetalert2.js';

import WizardIntentDetails from './WizardIntentDetails';

import {
    Row,
    Col,
} from 'react-bootstrap';

import {
    Link
} from 'react-router-dom';

export const WizardConfig = (props) => {

    const [selectedIntents, setSelectedIntents] = useState([]);

    useEffect(() => {
        props.getAllIntents(props.wizardIntentType);
    },[]);

    useEffect(() => {
        console.log("Wizard Config Start working on this project id =>", props.wizardIdProject);
    },[props.wizardIdProject]);

    const newIntent = (intent) => {

        let found = selectedIntents.findIndex((intenta) => intenta === intent);
        
        // if already clicked swal the popup
        if(found > -1) {
            const swalWithBootstrapButtons = Swal.mixin({
                customClass: {
                  confirmButton: 'wizard-pages-active-btn',
                },
                buttonsStyling: false
            });
            swalWithBootstrapButtons.fire({
                title: 'Intent already selected',
                confirmButtonText: 'Okay',
                // text: "Please select a page",
                // icon: 'warning',
            });
        } else {
            setSelectedIntents(selectedIntents => [...selectedIntents, intent ]);
        }
    };

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col className="wizard-pages-container mb-3" style={{backgroundColor: 'white'}}>
                
                {/* INTENTS */}
                <Row className="d-flex">
                    <Col lg={10} className="d-flex flex-column p-3">
                        
                        <div className="d-flex flex-column wizard-config-intents-container">
                            <p className="mr-auto ml-3 p-2 wizard-config-intent-title">Select Intents</p>
                            
                            <Row className="ml-1 mr-1 mb-3">
                                {
                                props.wizardIntents 
                                &&
                                props.wizardIntents.map((intent, index) => (
                                    <Col key={index} lg={3} className="d-flex ml-4 mb-3 pt-2 pb-2 wizard-config-intent-box" onClick={() => newIntent(intent) }>
                                        <p className="m-auto wizard-config-intent-name">{intent}</p>
                                    </Col>
                                ))}
                            </Row>
                        </div>
                        
                    </Col>

                    <Col />
                </Row>
                
                <Row className="d-flex mt-5">  
                    {selectedIntents &&
                        selectedIntents.map((intent,index) => 
                        <WizardIntentDetails key={index} intent={intent} wizardIdProject={props.wizardIdProject} stateManagment={setSelectedIntents} selectedIntents={selectedIntents} />
                    )}
                </Row>
             

                {/* BUTTONS */}
                <Row className="d-flex justify-content-between pl-4 pr-4">
                    <div className="wizard-pages-inactive-btn mb-3">
                        {/* MUST RESET ALL WIZARD VARS */}
                        <Link to="/home/pages" style={{textDecoration: 'none', color: '#818E94'}} onClick={() => props.resetWizardStep()} >
                            Back
                        </Link>
                    </div>

                    <div className="mb-3">
                        <Row>
                            <div className="wizard-pages-inactive-btn mr-3">
                                Save Draft
                            </div>

                            <div className="wizard-pages-active-btn" onClick={() => props.nextWizardStep()}>
                                Next
                            </div>
                        </Row>
                    </div>
                </Row>

            </Col>
    
            <Col lg={1} />
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardIdProject: state.wizardR.wizardIdProject,
    wizardIntents: state.wizardR.wizardIntents,
})

export default connect(mapStateToProps, {nextWizardStep, getAllIntents, })(WizardConfig)
