import React, { useEffect, useState } from 'react'
import '../Home.css';
import PageCard from './PageCard';
import {Helmet} from "react-helmet";


import { connect } from 'react-redux';
import { selectSocialMediaPage, getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';
// import OwlCarousel from 'react-owl-carousel';
import Lottie from 'react-lottie';

import animationData from '../../../assets/json/loading.json';
const ImageTest = require('../../../assets/images/Ant-Man.png');
const LogoTest = require('../../../assets/images/AntMan-logo.png');




function Pages(props) {

    const [loadingPages, setLoadingPages] = useState(false);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    useEffect(() => {
        props.getFbData();
        props.getInstaData();
        // setTimeout(() => {
        //     setLoadingPages(false);
        // },2000);
    },[]);

    useEffect(() => {
        if(props.fbData) {
            console.log("Pages fb data =>", props.fbData);
        }
    },[props.fbData]);
    
    useEffect(() => {
        if(props.instaData) {
            console.log("Pages insta Data =>", props.instaData);
        }
    },[props.instaData]);

    return (
    <>
        <Row>
            {
            props.title 
            ?
                <p className="home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
            :
                <p className="home-big-title">Pages Connected</p>
            }
        </Row>

        <Row>

            {
            !loadingPages
            ?
                (
                    <>
                    {/* <OwlCarousel margin={10} items={4}> */}
                    
                    {/* <div style={{ height: `22.7em` }}>

                    </div> */}

{/* <div className="container"> */}
        {/*slider-----------------*/}
        <ul id="autoWidth" className="cs-hidden">
          {/*1----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/Ant-Man.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/AntMan-logo.png" className="logo" width="100px" style={{height: 'auto'}} />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
          {/*2----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/DeadPool.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/Deadpool-logo.png" className="logo" />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
          {/*3----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/SpiderMan.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/SpiderMan-logo.png" className="logo" />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
          {/*4----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/Venom.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/Venom-logo.png" className="logo" />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
          {/*5----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/Thor.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/Thor-logo.png" className="logo" />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
          {/*6----------------------------*/}
          <li className="item-a">
            {/*slider-box*/}
            <div className="box">
              <p className="marvel">MARVEL</p>
              {/*model*/}
              <img src="img/IronMan.png" className="model" />
              {/*details*/}
              <div className="details">
                {/*logo-character*/}
                <img src="img/IronMan-logo.png" className="logo" />
                {/*character-details*/}
                <p>Bruce Bayne invite deadpool to kill the enemy how make distrub bat To the Kill The Anymens How MaKE him will be ie.</p>
              </div>
            </div>
          </li>
        </ul>
      {/* </div> */}


                    {/* <Col lg={12} className="p-0">
                        <div style={{ width: '100%', height: '300px' }}>
                            <ScrollHorizontal>
                                <div className="tile">
                                    <h2>ENIS {Math.random}</h2>
                                </div>   

                                <div className="tile">
                                    <h2>TOUHENT {Math.random}</h2>
                                </div>  

                                <div className="tile">
                                    <h2>HAIDER {Math.random}</h2>
                                </div> 

                                <div className="tile">
                                    <h2>NADHER {Math.random}</h2>
                                </div>    
                            </ScrollHorizontal>
                        </div>
                    </Col> */}

                    <Col lg={12} className="d-flex">


                        {/* Render Fb Data */}
                        {/* {props.fbData !== null && 
                            Object.entries(props.fbData).map( ([key, tab]) => 
                                tab.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",key)}>
                                    <PageCard platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={key}  team={page.user} admins={page.admins} setLoadingPages={setLoadingPages} />
                                </div>
                        ))} */}
                       

                        {/* Render Insta data */}
                        {/* {props.instaData !== null && 
                            Object.entries(props.instaData).map( ([key, tab]) => 
                                tab.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",key)}>
                                    <PageCard platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={key} team={page.user} admins={page.admins} setLoadingPages={setLoadingPages}  />
                                </div>
                        ))} */}
                    </Col>

                    {/* </OwlCarousel> */}
                    </>
                )
            :
                (
                <div className="d-flex" style={{ width: '100%' }}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={200} />
                    </div>
                </div>
                )
            }
        </Row>
    </>
    );
}

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { selectSocialMediaPage,getFbData, getInstaData })(Pages);
