import React, { useEffect, useState } from 'react'
import TopMenu from '../components/TopMenu'
import { connect } from 'react-redux'
import { getAllConnectedPages } from '../actions/index'
function WizardsHome(props) {
    // const [checkedPage, setCheckedPage]=useState(null)
    useEffect(() => {
        props.getConnctedPages()
    }, [])
    const handelChange = (e) => {
        const { value } = e.target;
        const pageChecked = props.connectedPages.find(el => el._id == value)
        console.log("page checked ", pageChecked)
    }
    return (
        <div>
            <TopMenu />
            <div style={{ paddingLeft: 30, paddingRight: 30, paddingTop: 30 }}>
                <h1 style={{ fontSize: 30, color: "rgba(45, 47, 57, 1)", textAlign: "center" }}>iModo Wizard</h1>
                <svg style={{
                    marginTop: 50,
                    marginBottom: 50,
                    marginLeft: "auto",
                    marginRight: "auto",
                    display: "block"
                }} xmlns="http://www.w3.org/2000/svg" width="828" height="63" viewBox="0 0 828 63">
                    <g id="Groupe_1143" data-name="Groupe 1143" transform="translate(-684 -251)">
                        <g id="step" transform="translate(-509 -438)">
                            <text id="Page_Selection" data-name="Page Selection" transform="translate(1249 747)" fill="#e5007d" font-size="15" font-family="Poppins-Medium, Poppins" font-weight="500"><tspan x="-55.898" y="0">Page Selection</tspan></text>
                            <circle id="Ellipse" cx="19" cy="19" r="19" transform="translate(1230 689)" fill="#e5007d" opacity="0.25" />
                            <text id="_1" data-name="1" transform="translate(1249 714)" fill="#e5007d" font-size="16" font-family="Poppins-SemiBold, Poppins" font-weight="600"><tspan x="-2.896" y="0">1</tspan></text>
                            <path id="Line" d="M0,0H1V180H0Z" transform="translate(1457.702 708.5) rotate(90)" fill="#e9e9f0" />
                        </g>
                        <g id="step-2" data-name="step" transform="translate(-273 -438)">
                            <text id="Post_Selection" data-name="Post Selection" transform="translate(1249 747)" fill="#b4b4b4" font-size="15" font-family="Poppins-Medium, Poppins" font-weight="500"><tspan x="-52.68" y="0">Post Selection</tspan></text>
                            <circle id="Ellipse-2" data-name="Ellipse" cx="19" cy="19" r="19" transform="translate(1230 689)" fill="#b4b4b4" opacity="0.25" />
                            <text id="_2" data-name="2" transform="translate(1249 714)" fill="#818e94" font-size="16" font-family="Poppins-SemiBold, Poppins" font-weight="600"><tspan x="-4.592" y="0">2</tspan></text>
                            <path id="Line-2" data-name="Line" d="M0,0H1V180H0Z" transform="translate(1457.702 708.5) rotate(90)" fill="#e9e9f0" />
                        </g>
                        <g id="step-3" data-name="step" transform="translate(-37 -438)">
                            <text id="Automation_config" data-name="Automation config" transform="translate(1249 747)" fill="#b4b4b4" font-size="15" font-family="Poppins-Medium, Poppins" font-weight="500"><tspan x="-70.628" y="0">Automation config</tspan></text>
                            <circle id="Ellipse-3" data-name="Ellipse" cx="19" cy="19" r="19" transform="translate(1230 689)" fill="#b4b4b4" opacity="0.25" />
                            <text id="_3" data-name="3" transform="translate(1249 714)" fill="#818e94" font-size="16" font-family="Poppins-SemiBold, Poppins" font-weight="600"><tspan x="-4.792" y="0">3</tspan></text>
                            <path id="Line-3" data-name="Line" d="M0,0H1V180H0Z" transform="translate(1457.702 708.5) rotate(90)" fill="#e9e9f0" />
                        </g>
                        <g id="step-4" data-name="step" transform="translate(199 -438)">
                            <text id="Test_validation" data-name="Test &amp; validation" transform="translate(1249 747)" fill="#b4b4b4" font-size="15" font-family="Poppins-Medium, Poppins" font-weight="500"><tspan x="-63.217" y="0">Test &amp; validation</tspan></text>
                            <circle id="Ellipse-4" data-name="Ellipse" cx="19" cy="19" r="19" transform="translate(1230 689)" fill="#b4b4b4" opacity="0.25" />
                            <text id="_4" data-name="4" transform="translate(1249 714)" fill="#818e94" font-size="16" font-family="Poppins-SemiBold, Poppins" font-weight="600"><tspan x="-5.288" y="0">4</tspan></text>
                        </g>
                    </g>
                </svg>
                <div style={{ background: '#fff', paddingTop: 20, paddingLeft: 70, paddingRight: 70, paddingBottom: 20 }}>
                    <h2 style={{
                        fontFamily: "Poppins",
                        fontStyle: "normal",
                        fontWeight: "bold",
                        fontSize: "16",
                        color: "rgba(75,79,86,1)"
                    }}>Select a page to moderate.</h2>
                    <div style={{ paddingLeft: 50, paddingRight: 50 }}>
                        <h2 style={{
                            fontFamily: "Poppins",
                            fontStyle: "normal",
                            fontWeight: "bold",
                            fontSize: "18",
                            color: "rgba(28,28,27,1)"
                        }}>All Pages</h2>
                        <div>
                            {
                                props.connectedPages.map(el => <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                                    <div style={{
                                        display: "flex",
                                        alignItems: "center",
                                        width: "130px",
                                        justifyContent: "space-between"
                                    }}>
                                        <img src={'https://graph.facebook.com/v3.2/' + el.idPage + '/picture?access_token=' + el.access_token} alt="page thumbnail" style={{ width: 40 }} />
                                        <span style={{
                                            fontFamily: "Roboto",
                                            fontStyle: "normal",
                                            fontWeight: "normal",
                                            fontSize: "16px",
                                            color: "rgba(75,79,86,1)"
                                        }}>{el.name}</span>
                                    </div>
                                    <input type="radio" name="connectedPages" value={el.idPage} onChange={handelChange} />
                                </div>)

                            }
                            {/* <input type="radio" name="" id=""/> */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapDispatchToprops = (dispatch) => {
    return {
        getConnctedPages: () => dispatch(getAllConnectedPages())
    }
}

const mapStateToProps = (state) => {
    return {
        connectedPages: state.allConnectedPages
    }
}

export default connect(mapStateToProps, mapDispatchToprops)(WizardsHome)
