import React, { useEffect , useState} from 'react';
import '../Home.css';

import { connect } from 'react-redux';                       
import { detectCurrency,} from '../../../redux/actions/billingActions';

import {
    Row,
    Col,
    Table,
    Image,
} from 'react-bootstrap';

// Translations
import { trans } from '../../../Translations';

import { IoIosArrowBack } from 'react-icons/io';

function Invoice(props) {
    
    let {
        lang,
        currency,
        
        // Functions:
        setInvoice,
        detectCurrency,
    } = props;
    
    const userData = JSON.parse(localStorage.getItem('userData'));

    // useEffect(() => {
    //     detectCurrency()
    //     .then(() => {
    //         getPricingInfos()
    //         .then(() => {
    //             payPlan();
    //         });
    //     })
    // },[]);


    return (
        <div style={{ marginTop: '140px' }}>
           <Row>
               <Col lg={12} className="mb-5 d-flex" onClick={() => setInvoice(false)}>
                   <IoIosArrowBack color="#818e94" size={20} className="my-auto" />
                   <p className="my-auto navbar-menu-title" style={{ fontSize: "18px", color: "#818e94"}}>Back</p>
               </Col>

               <Col lg={10} style={{ border: "1px solid #dbdbdb"}}>
                    
                        <div className="d-flex mb-5">
                            <div className="mr-auto my-auto">
                                
                            </div>
                            <div className="ml-auto my-auto">
                                <div>
                                    <p className="m-auto">INVOICE #33070</p>
                                    <p className="m-auto">Created: Aug 4, 2020</p>
                                </div>
                            </div>
                        </div>

                        <div>
                            <Col lg={lang === "fr" ? 6 : 5} className="p-0">
                                <Col lg={12} className="d-flex mb-2 p-0">
                                    <p className="mr-auto billing-plan-lightgray-text">{trans[lang].billingPlan.socialPages}</p>
                                    <p className="ml-auto billing-plan-lightgray-text" style={{color: "black"}}>{userData.user.pack[0].number_pages}</p>
                                </Col>
                            </Col>

                            <Col lg={lang === "fr" ? 6 : 5} className="p-0">
                                <Col lg={12} className="d-flex mb-2 p-0">
                                    <p className="mr-auto billing-plan-lightgray-text">{trans[lang].billingPlan.teamMembers}</p>
                                    <p className="ml-auto billing-plan-lightgray-text" style={{color: "black"}}>{userData.user.pack[0].number_user}</p>
                                </Col>
                            </Col>

                            <Col lg={lang === "fr" ? 6 : 5} className="p-0">
                                <Col lg={12} className="d-flex mb-2 p-0">
                                    <p className="mr-auto billing-plan-lightgray-text">{trans[lang].billingPlan.activeAgents}</p>
                                    <p className="ml-auto billing-plan-lightgray-text" style={{color: "black"}}>{userData.user.pack[0].number_agent}</p>
                                </Col>
                            </Col>

                            <Col lg={lang === "fr" ? 6 : 5} className="p-0">
                                <Col lg={12} className="d-flex mb-2 p-0">
                                    <p className="mr-auto billing-plan-lightgray-text">{trans[lang].billingPlan.monthlyReplies}</p>
                                    <p className="ml-auto billing-plan-lightgray-text" style={{color: "black"}}>{userData.user.pack[0].number_autoReply}</p>
                                </Col>
                            </Col>
                        </div>

                        <div className="d-flex">
                            <div className="ml-auto d-flex">
                                <p className="billing-plan-lightgray-text my-auto">{trans[lang].billingPlan.planValid} &nbsp;</p>
                                <p className="billing-plan-pink-text my-auto">Avr 18, 1996</p>
                            </div>
                        </div>

                        <hr style={{ width: "100%", border: "1px solid #B4B4B4" }} />

                        <div className="d-flex">
                            <div className="ml-auto d-flex">
                                <div className="billing-plan-green-btn py-2 px-4" onClick={() => setChoice({ id:"change", value: trans[lang].billing.changePlan})}>
                                    <p className="m-auto">{trans[lang].billingPlan.upgradePlan}</p>
                                </div>
                            </div>
                        </div>
                </Col>
           </Row>
        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        lang : state.socialMediaR.lang,
        currency : state.billingR.currency,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { detectCurrency })(Invoice);
