import React, { useState, useEffect } from 'react';

import axios from 'axios';
import { host } from '../../../config';

import { connect } from 'react-redux';
import { nextSpecificWizardStep, testAgent, resetStep4ReceivedMsg } from '../../../redux/actions/wizardActions';

import {
    Row,
    Col,
    Image,
    Spinner,
    OverlayTrigger,
    Popover,
} from 'react-bootstrap';

import {
    Link,
    withRouter,
} from 'react-router-dom';

import Swal from 'sweetalert2/dist/sweetalert2.js';

const BALISE_ICON = require('../../../assets/images/home/balise.png');
const BALISE_ICON_ACTIVE = require('../../../assets/images/home/balise_active.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const IMODO_PROFILE_ICON = require('../../../assets/images/home/full-bulle-imodo.png');

export const WizardTest = (props) => {

    let {
        wizardIntentType,
        wizardSelectedPage,
        wizardSelectedPost,
        wizardReceivedMsg,
        wizardIdProject,
        history,

        resetStep4ReceivedMsg,
        nextSpecificWizardStep,
    } = props;

    // Popup Config:
    const swalWithBootstrapButtons = Swal.mixin({ customClass: {confirmButton: 'wizard-pages-active-btn-alert'}, buttonsStyling: false });
    
    const [message,setMessage] = useState('');
    const [sendedMsg,setSendedMsg] = useState(null);
    const [btnLoading,setBtnLoading] = useState(null);
    const [wizardFinish,setWizardFinish] = useState(null);
    
    // Tags:
    const [Tags,setTags] = useState(false);
    
    const TagsPopover = (
        <Popover id="popover-tags">
          <Popover.Content>
            <Row>
                <Col lg={12}>
                    <Row className="pl-1 my-2">
                        <Col xs={12} className="d-flex mb-1 mt-2">
                            <p className="my-auto mr-auto wizard-config-tag" onClick={() => { setMessage(prevMsg => prevMsg + '##FIRST_NAME##'); }}>First Name</p>
                        </Col>
                         
                        <Col xs={12} className="d-flex mb-1">
                            <p className="my-auto mr-auto wizard-config-tag" onClick={() => { setMessage(prevMsg => prevMsg + '##LAST_NAME##'); }}>Last Name</p>
                        </Col>

                        <Col xs={12} className="d-flex mb-1">
                            <p className="my-auto mr-auto wizard-config-tag" onClick={() => { setMessage(prevMsg => prevMsg + '##FULL_NAME##'); }}  >Full Name</p>
                        </Col>

                        <Col xs={12} className="m-auto d-flex mb-2">
                            <p className="my-auto mr-auto wizard-config-tag" onClick={() => { setMessage(prevMsg => prevMsg + '##PAGE_NAME##'); }}  >Page Name</p>
                        </Col> 
                    </Row>
                </Col>
            </Row>
          </Popover.Content>
        </Popover>
    );

    const chat = () => {
        props.testAgent(message,wizardIntentType,wizardSelectedPage,wizardSelectedPost)
        .then(() => {
            setSendedMsg(message);
        })
        .catch(err => {
            swalWithBootstrapButtons.fire({
                title: 'Error while finishing the wizard. Try Again!',
                confirmButtonText: 'Okay',
            });
        });
    };

    const showTheFinish = () => {
        setBtnLoading(true);

        setTimeout(() => {
            setWizardFinish(true);
            setBtnLoading(null);
        },1500);
    };

    const activateAgentNow = (status) => {

        if(status === 'live') {

            let objectToSend = {
                live : true
            };

            axios.put(host + `/api/v1/secure/project/agent/live/${wizardIdProject}`,objectToSend,{
                headers: {
                    'authorization': 'Bearer ' + localStorage.getItem('authtoken')
                }
            })
            .then(res => {
                console.log('agent Live post APi Response =>', res.data.data);
                history.push('/home/my-agents');
            })
            .catch((err) => {
                console.log("agent Live post Api error  =>", err)
                swalWithBootstrapButtons.fire({ title: `Error while activating agent. Try Again !`,confirmButtonText: 'Okay' });
            })
            
        } else {

            let objectToSend = {
                live : false
            };

            axios.put(host + `/api/v1/secure/project/agent/live/${wizardIdProject}`,objectToSend,{
                headers: {
                    'authorization': 'Bearer ' + localStorage.getItem('authtoken')
                }
            })
            .then(res => {
                console.log('agent Later post APi Response =>', res.data.data);
                history.push('/home/my-agents');
            })
            .catch((err) => {
                console.log("agent Later post Api error  =>", err)
                swalWithBootstrapButtons.fire({ title: `Error while activating agent. Try Again !`,confirmButtonText: 'Okay' });
            })
        }

    };

    const backToStep3 = () => {
        // Reset Msg received & Go:
        resetStep4ReceivedMsg();
        nextSpecificWizardStep(2)
    };

    useEffect(() => {
        window.scrollTo(0, 0);
    },[])

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col lg={9} className="wizard-pages-container mb-3" style={{backgroundColor: 'white'}}>
                
                <Row className="d-flex mt-4 ml-2 mb-4">
                    <p className="my-auto wizard-pages-title">Test Interaction</p>
                </Row>
                
                <Row className="mb-4">
                    <Col lg={9} className="mx-auto" style={{backgroundColor : 'white',borderRadius: '0.2rem',border: '1px solid #B4B4B4', height: wizardReceivedMsg && wizardReceivedMsg.length > 1 ? "auto" : '250px' }}>
                        {sendedMsg === null
                        &&
                        <Row className="d-flex" style={{height: '100%'}}>
                            <Col lg={12} className="d-flex" >
                                <p className="m-auto wizard-posts-status-text">Test your interaction...</p>
                            </Col>
                        </Row>
                        }

                        {/* BULLE GRIS */}
                        {sendedMsg !== null &&
                        <Row className="d-flex">
                            <Col>
                                <Row className="my-3">
                                    <Col>
                                        <div className="my-agents-msgs-bulle mb-1">
                                            <p className="my-agents-msgs-bulle-text">
                                               {sendedMsg}
                                            </p>
                                        </div>
                                        {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                    </Col>
                                </Row>
                            </Col>

                            <Col lg={7} />
                        </Row>
                        }

                        {/* BULLE BLEU */}
                        {wizardReceivedMsg && wizardReceivedMsg.length > 0
                        &&
                        wizardReceivedMsg.map((msg,index) => 
                            msg.response.text === ""
                            ?
                            <Row key={index} className="d-flex">
                                <Col lg={6} />

                                <Col lg={6}>
                                    <Row className="my-3">
                                        <Col lg={12}>
                                            <Row className="justify-content-end">
                                                <Col lg={10} className="px-0">
                                                    <div className="my-agents-msgs-bulle-bleu mb-1" style={{backgroundColor: '#3B86FF'}}>
                                                        <p className="my-agents-msgs-bulle-text" style={{color: 'white'}}>
                                                            J'ai pas compris...
                                                        </p>
                                                    </div>
                                                </Col>
                                                <Col lg={2} className="d-flex pl-1">
                                                    <Image src={IMODO_PROFILE_ICON} className={"mt-auto"} style={{maxWidth: '50px'}} />
                                                </Col>
                                            </Row>
                                            {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                        </Col>
                                    </Row>
                                </Col>
                            </Row>
                            :
                            <Row key={index} className="d-flex">
                                <Col lg={6} />

                                <Col lg={6}>
                                    <Row className="my-3">
                                        <Col lg={12}>
                                            <Row className="justify-content-end">
                                                <Col lg={10} className="px-0">
                                                    <div className="my-agents-msgs-bulle-bleu mb-1" style={{backgroundColor: '#3B86FF'}}>
                                                        <p className="my-agents-msgs-bulle-text" style={{color: 'white'}}>
                                                            {msg.response.text}
                                                        </p>
                                                    </div>
                                                </Col>
                                                <Col lg={2} className="d-flex pl-1">
                                                    <Image src={IMODO_PROFILE_ICON} className={"mt-auto"} style={{maxWidth: '50px'}} />
                                                </Col>
                                            </Row>
                                            {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                        </Col>
                                    </Row>
                                </Col>
                            </Row>
                        )}
                    </Col>
                </Row>

                <Row className="mt-3 mb-3">
                    <Col lg={9} className="mx-auto" style={{backgroundColor : 'white',borderRadius: '0.2rem',border: '1px solid #B4B4B4'}}>
                        <Row className="pt-4 pb-4">
                            <Col lg={2} style={{background: 'red'}}>
                                {/* <Row className="justify-content-around"> */}
                                <Row className="d-flex">
                                    {/* <div className="d-flex">
                                        <OverlayTrigger trigger="click" placement="top" overlay={TagsPopover}>
                                            <Image 
                                                src={Tags ? BALISE_ICON_ACTIVE : BALISE_ICON} 
                                                onClick={() => setTags(!Tags)} 
                                                className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                                                style={{ width: '19px', cursor: 'pointer'}} 
                                            />   
                                        </OverlayTrigger> 
                                    </div>                                

                                    <div className="d-flex">
                                        <Image src={EMO_ICON} className="m-auto my-agents-msgs-tiny-icon" />
                                    </div> */}

                                    <div className="d-flex mr-auto">
                                        <Image src={LETTER_ICON} className="my-auto ml-2 my-agents-msgs-tiny-icon" />
                                    </div>
                                </Row>
                            </Col>

                            <Col lg={10} className="d-flex p-0" style={{background: 'gray'}}>
                                <input 
                                    type="text" 
                                    className="my-auto wizard-test-input w-100" 
                                    placeholder="Your Message..." 
                                    onChange={(e) => setMessage(e.target.value)} value={message}
                                    onKeyDown={(e) => e.key === 'Enter' && chat()}
                                />
                            </Col>

                            <Col lg={3} />    

                        </Row>
                    </Col>
                </Row>

                <Row className="d-flex justify-content-between pl-4 pr-4">
                    <div className="wizard-pages-inactive-btn py-2 px-4 mb-3" onClick={() => backToStep3()}>
                        {/* MUST RESET ALL WIZARD VARS */}
                        Back
                    </div>

                    <div className="mb-3">
                        <Row>
                            <div className="wizard-pages-inactive-btn py-2 px-4 mr-3">
                                Save Draft
                            </div>

                            <div className={"wizard-pages-active-btn py-2 px-4 mr-3"} onClick={() => showTheFinish()}>
                                {
                                btnLoading
                                ?
                                (<Spinner size="sm" animation="border" variant="light" />)
                                :
                                "Finish"
                                }
                            </div>
                        </Row>
                    </div>
                </Row>

            </Col>
    
            {wizardFinish 
            &&    
            <Col lg={2} className="d-flex">
                <div className="mt-auto mb-5">
                    <div className="finish-test-container d-flex flex-column py-3 mb-5">
                        <button className="mx-auto finish-test-white-btn mb-2" onClick={() => activateAgentNow('live')}>Activate Now</button>
                        <button className="mx-auto finish-test-transparent-btn" onClick={() => activateAgentNow('later')}>Activate Later</button>
                    </div>
                    {/* 
                    <Col lg={12} className="d-flex">
                    </Col>

                    <Col lg={12} className="d-flex">
                    </Col> */}
                </div>
            </Col>
            }
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardSelectedPage: state.wizardR.wizardSelectedPage,
    wizardSelectedPost: state.wizardR.wizardSelectedPost,
    wizardReceivedMsg: state.wizardR.wizardReceivedMsg,
    wizardIdProject: state.wizardR.wizardIdProject,
})

export default withRouter(connect(mapStateToProps, { nextSpecificWizardStep, testAgent, resetStep4ReceivedMsg })(WizardTest));


