                        <Row className="d-flex">
                            <Col>
                                <Row className="my-3">
                                    <Col>
                                        <div className="my-agents-msgs-bulle mb-1">
                                            <p className="my-agents-msgs-bulle-text">
                                               {props.wizardReceivedMsg}
                                            </p>
                                        </div>
                                        <p className="my-agents-msgs-bulle-date">1 hour ago</p>
                                        </Col>
                                        </Row>
                                    </Col>
        
                                    <Col lg={7} />
                                </Row>
                                }

