import React, { Component } from 'react'
import { connect } from 'react-redux';
import { nextWizardStep } from '../../../redux/actions/wizardActions';


import {
    Row,
    Col,
    Image
} from 'react-bootstrap';

import { FaTrashAlt } from 'react-icons/fa';
import { IoIosArrowDown } from 'react-icons/io';

export const WizardConfig = (props) => {

    const intents = [
        'Price',
        'Location',
        'Time',
        'Event',
        'Greeting',
    ];

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col className="wizard-pages-container" style={{backgroundColor: 'white'}}>
                
                {/* INTENTS */}
                <Row className="d-flex">
                    <Col lg={6} className="d-flex flex-column p-3">
                        
                        <div className="d-flex flex-column wizard-config-intents-container">
                            <p className="mr-auto ml-3 p-2 wizard-config-intent-title">Select Intents</p>
                            
                            <Row className="ml-1 mr-1 mb-3">
                                {intents.map((item, index) => (
                                    <Col lg={3} className="d-flex ml-4 mb-3 pt-2 pb-2 wizard-config-intent-box">
                                        <p className="m-auto wizard-config-intent-name">{item}</p>
                                    </Col>
                                ))}
                            </Row>
    
                        </div>
                        
                    </Col>

                    <Col />
                </Row>

                {/* INTENTS DETAILS */}
                <Row className="d-flex mt-5" style={{backgroundColor: 'red'}}>    
                    <Col lg={6}>
                        <div className="d-flex flex-column wizard-config-intents-container">
                                <Row className="justify-content-between">
                                    <p className="mr-auto ml-3 p-2 wizard-config-intent-title">Select Intents</p>
                                    
                                    <div>
                                        <FaTrashAlt  color={"#818E94"} size={"30"} />
                                        <IoIosArrowDown color={"#818E94"} size={"30"} />
                                    </div>

                                </Row>
                                
                                <Row className="ml-1 mr-1 mb-3">
                                    {intents.map((item, index) => (
                                        <Col lg={3} className="d-flex ml-4 mb-3 pt-2 pb-2 wizard-config-intent-box">
                                            <p className="m-auto wizard-config-intent-name">{item}</p>
                                        </Col>
                                    ))}
                                </Row>
                        </div>
                    </Col>
                </Row>

            </Col>
    
            <Col lg={1} />
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
})

export default connect(mapStateToProps, {nextWizardStep})(WizardConfig)
