import React from 'react'
import { connect } from 'react-redux'
import { connectPage } from '../actions'

function PageCart(props) {
    return (
        <div>
            <img src={'https://graph.facebook.com/v3.2/' + page._id + '/picture?access_token=' + page.access_token} alt="" />
            <h1>{props.name}</h1>
            {
                props.connected ?
                    <button>Disconnect Your page</button> :
                    <button onClick={() => props.connect(props.page)}>Connect Your page</button>
            }
        </div>
    )
}

const mapDispatchToProps = (dispatch) => {
    return {
        connect: (page) => dispatch(connectPage(page))
    }
}

export default connect(null, mapDispatchToProps)(PageCart)
